import React from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  Accordion,
  Radio,
  Group,
  Center,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignLeft,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,

} from "@tabler/icons-react";
const Drawer = () => {
  return (
    <Box bg="gray.3">
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Drawer Icon
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="shape-type">
          <Accordion.Control>Change Icon</Accordion.Control>
          <Accordion.Panel>
            <Flex
              mih={50}
            //   bg="rgba(0, 0, 0, .3)"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              <IconAlignLeft style={{ border: "1px solid #ccc" }}/>
              <IconAlignCenter style={{ border: "1px solid #ccc" }}/>
              <IconAlignJustified style={{ border: "1px solid #ccc" }}/>
              <IconAlignRight style={{ border: "1px solid #ccc" }}/>
            </Flex>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </Box>
  );
};

export default Drawer;
