import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Checkbox,
} from "@mantine/core";

const categories = [
  {
    name: "Electronics",
    imageSrc:
      "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
  },
  {
    name: "Accessories",
    imageSrc:
      "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
  },
  {
    name: "Clothing",
    imageSrc:
      "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
  },
  {
    name: "Shoes",
    imageSrc:
      "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
  },
];

const CategoryList = ({ setBoxVisible, boxVisible }) => {
  const [iconCheckVisible, setIconCheckVisible] = useState(false);

  const handleCategoryWithProductClick = () => {
    setBoxVisible(!boxVisible);
    setIconCheckVisible(!iconCheckVisible);
  };

  return (
    <Accordion.Item value="category-list">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Category List</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel bg="gray.2">
        <Flex align="center" justify="center">
          
            <Box
              key={index}
              ml="xs"
              bg="#FFFFFF"
              style={{ cursor: "pointer" }}
              onClick={handleCategoryWithProductClick}
            >
              {categories.map((category, index) => (
              <Flex
                h={{ base: "auto", md: 100 }}
                justify="center"
                align="center"
                direction={{ base: "column", md: "row" }}
                gap="sm"
              >
                <Flex justify="center" align="center" direction="column">
                  <Avatar
                    variant="filled"
                    radius="sm"
                    size="lg"
                    color="yellow"
                    src={category.imageSrc}
                  />
                  <Text size="xs">{category.name}</Text>
                </Flex>
                {iconCheckVisible && (
                  <Flex h={{ base: "auto", md: `100%` }}>
                    <Checkbox defaultChecked />
                  </Flex>
                )}
              </Flex>
              ))}
            </Box>
          
        </Flex>
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default CategoryList;
