import { IconAlignJustified, IconSearch, IconCameraSelfie } from "@tabler/icons-react";
import { Accordion, Container, Text, rem } from "@mantine/core";
import {
  IconAlignLeft,
  IconPhotoMinus,
  IconCircleXFilled,
} from "@tabler/icons-react";

const SideAppBar = () => {
  return (
    <div>
      <Container
        my="xs"
        bg="var(--mantine-color-blue-light)"
        className="rounded-lg p-4 transition duration-300 ease-in-out hover:border-gray-300"
        style={{ borderRadius: "5px" }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-end",
          }}
        >
          <div className="mr-4" style={{ alignItems: "flex-start" }}>
            <IconAlignLeft />
          </div>
          <div style={{ display: "inline-block" }}>
            <IconSearch style={{ display: "inline-block" }} />
            <IconPhotoMinus style={{ display: "inline-block" }} />
          </div>
        </div>
      </Container>
      <Accordion variant="contained">
        <Accordion.Item value="photos">
          <Accordion.Control
            icon={
              <IconAlignJustified
                style={{
                  color: "var(--mantine-color-red-filled",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Drawer Icon
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="print">
          <Accordion.Control
            icon={
              <IconSearch
                style={{
                  color: "var(--mantine-color-blue-filled",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Search
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="camera">
          <Accordion.Control
            icon={
              <IconCameraSelfie
                style={{
                  color: "var(--mantine-color-teal-filled)",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Camera settings
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </div>
  );
};

export default SideAppBar;
