import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";
import ResponsivePhone from "./ResponsivePhone";
import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
  IconCheck,
} from "@tabler/icons-react";
const Home = () => {
  {}
  const [layouttVisible, setLayoutVisible] = useState(true);
  const [iconCheckVisible, setIconCheckVisible] = useState(false);
  const [iconCheckVisible2, setIconCheckVisible2] = useState(false);
  const [iconCheckVisible3, setIconCheckVisible3] = useState(false);
  const [iconCheckVisible4, setIconCheckVisible4] = useState(false);

  const handleLayoutClick = () => {
    setLayoutVisible(false);
  };

  const [selectedIcon, setSelectedIcon] = useState(null);

  const [boxValue, setBoxValue] = useState(1);
  const [paddingValue, setPaddingValue] = useState(1);
  const [marginValue, setMarginValue] = useState(1);
  const [categoryWithProductRadiusValue, setCategoryWithProductRadiusValue] =
    useState(1);
  const [productNameTextSizeValue, setProductNameTextSizeValue] = useState(1);

  const [categoryListBoxVisible, setCategoryListBoxVisible] = useState(false);
  const [categoryWithProductVisible, setCategoryWithProductVisible] =
    useState(false);
  {/* categoryHeader Edit */}
  const [categoryHeader, setCategoryHeader] = useState({
    electronic_level: "Electronics",
    accessories_level: "Accessories",
    clothing_level: "Clothing",
    shoes_level: "Shoes",
    // electronic_level: "Electronics",
  });

  const updateProperty = (property, value) => {
    setCategoryHeader({ ...categoryHeader, [property]: value });
  };

  const onElectrnicChange = (text) => {
    updateProperty("electronic_level", text);
  };

  const onAccessoriesChange = (text) => {
    updateProperty("accessories_level", text);
  };

  const onClothingChange = (text) => {
    updateProperty("clothing_level", text);
  };

  const onShoesChange = (text) => {
    updateProperty("shoes_level", text);
  };

  {/* categoryWithProductHeader Edit */}
  const [categoryWithProductHeaderValue, setCategoryWithProductHeaderValue] =
    useState("Hot Sales");

  const handleHeaderChange = (event) => {
    setCategoryWithProductHeaderValue(event.target.value);
  };

  const handleRadiusChange = (newValue) => {
    setBoxValue(newValue);
  };

  const handleRadiusIncrement = () => {
    const newValue = boxValue + 1;
    if (newValue <= 100) {
      setBoxValue(newValue);
    }
  };

  const handlePaddingChange = (newValue) => {
    setPaddingValue(newValue);
  };

  const handlePaddingIncrement = () => {
    const newValue = paddingValue + 1;
    if (newValue <= 100) {
      setPaddingValue(newValue);
    }
  };

  const handleMarginChange = (newValue) => {
    setMarginValue(newValue);
  };

  const handleMarginIncrement = () => {
    const newValue = marginValue + 1;
    if (newValue <= 100) {
      setMarginValue(newValue);
    }
  };

  const handleCategoryWithProductRadiusChange = (newValue) => {
    setCategoryWithProductRadiusValue(newValue);
  };

  const handleCategoryWithProductRadiusIncrement = () => {
    const newValue = categoryWithProductRadiusValue + 1;
    if (newValue <= 100) {
      setCategoryWithProductRadiusValue(newValue);
    }
  };

  const handleProductNameTextSizeChange = (newValue) => {
    setProductNameTextSizeValue(newValue);
  };

  const handleProductNameTextSizeIncrement = () => {
    const newValue = productNameTextSizeValue + 1;
    if (newValue <= 20) {
      setProductNameTextSizeValue(newValue);
    }
  };

   {/* CategoryList Click */}
  const handleCategoryListClick = () => {
    setCategoryListBoxVisible(true);
    setCategoryWithProductVisible(false);
    setIconCheckVisible(!iconCheckVisible);
    setIconCheckVisible2(false);
    setIconCheckVisible3(false);
    setIconCheckVisible4(false);
  };

  const handleCategoryList2Click = () => {
    setCategoryListBoxVisible(true);
    setCategoryWithProductVisible(false);
    setIconCheckVisible2(!iconCheckVisible2);
    setIconCheckVisible(false);
    setIconCheckVisible3(false);
    setIconCheckVisible4(false);
  };

  const handleCategoryList3Click = () => {
    setCategoryListBoxVisible(true);
    setCategoryWithProductVisible(false);
    setIconCheckVisible3(!iconCheckVisible3);
    setIconCheckVisible(false);
    setIconCheckVisible2(false);
    setIconCheckVisible4(false);
  };

  const handleCategoryList4Click = () => {
    setCategoryListBoxVisible(true);
    setCategoryWithProductVisible(false);
    setIconCheckVisible4(!iconCheckVisible4);
    setIconCheckVisible(false);
    setIconCheckVisible2(false);
    setIconCheckVisible3(false);
  };
  {/* CategoryWithProductClick */}

  const [showCheckbox, setShowCheckbox] = useState(false);
  const [checkboxChecked, setCheckboxChecked] = useState(false);

  const [showCheckbox2, setShowCheckbox2] = useState(false);
  const [checkboxChecked2, setCheckboxChecked2] = useState(false);

  const handleCategoryWithProductClick = () => {
    setCategoryWithProductVisible(!categoryWithProductVisible);
    setCategoryListBoxVisible(false);
    setShowCheckbox(!showCheckbox);
    setCheckboxChecked(!checkboxChecked);
    setShowCheckbox2(false);
    setCheckboxChecked2(false);
  };

  const handleCategoryWithProduct2Click = () => {
    setCategoryWithProductVisible(!categoryWithProductVisible);
    setCategoryListBoxVisible(false);
    setShowCheckbox(false);
    setCheckboxChecked(false);
    setShowCheckbox2(!showCheckbox2);
    setCheckboxChecked2(!checkboxChecked2);
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon:", component);
    setSelectedIcon(index);
  };

  const textStyles = {
    normal: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "none",
    },
    bold: {
      fontWeight: "bold",
      fontStyle: "normal",
      textDecoration: "none",
    },
    italic: {
      fontWeight: "normal",
      fontStyle: "italic",
      textDecoration: "none",
    },
    underline: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "underline",
    },
  };

  const icons = [
    { name: "IconLetterN", component: <IconLetterN />, style: "normal" },
    { name: "IconBold", component: <IconBold />, style: "bold" },
    { name: "IconItalic", component: <IconItalic />, style: "italic" },
    { name: "IconUnderline", component: <IconUnderline />, style: "underline" },
  ];

  const [showProductNameEdit, setShowProductNameEdit] = useState(false);
  const [showProductRateEdit, setShowProductRateEdit] = useState(false);
  const [showCategoryNameEdit, setShowCategoryNameEdit] = useState(false);

  const handleProductNameEdiClick = () => {
    setShowProductNameEdit(!showProductNameEdit);
    setShowProductRateEdit(false);
  };

  const handleProductRateEdiClick = () => {
    setShowProductRateEdit(!showProductRateEdit);
    setShowProductNameEdit(false);
  };

  const handleCategoryNameEdiClick = () => {
    setShowCategoryNameEdit(!showCategoryNameEdit);
    setShowProductRateEdit(false);
    setShowProductNameEdit(false);
  };

  {/* Dropzone */}

  const [files, setFiles] = useState([]);

  const previews = files.map((file, index) => {
    const imageUrl = URL.createObjectURL(file);
    return (
      <Image
        key={index}
        src={imageUrl}
        onLoad={() => URL.revokeObjectURL(imageUrl)}
      />
    );
  });

  return (
    <Grid>
      <Grid.Col span={4} h={550} style={{ overflowY: "auto" }}>
        {layouttVisible && (
          <Box m="xs" bg="gray.2" style={{ borderRadius: "10px" }}>
            {/* Layouts */}
            <Box
              bg="indigo.5"
              ml="xs"
              mr="xs"
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                backgroundColor: "indigo.5",
                padding: "10px",
                borderRadius: "10px",
              }}
            >
              <Text
                fw={900}
                c={`#FFFFFF`}
                style={{
                  flex: "1",
                  textAlign: "center",
                }}
              >
                Layouts
              </Text>
              <IconX
                size={28}
                onClick={handleLayoutClick}
                style={{
                  borderRadius: "50%",
                  background: "red",
                  color: "white",
                  alignSelf: "flex-end",
                  cursor: "pointer",
                }}
              />
            </Box>
            {/* Slider */}
            <Accordion m="xs" pb={10} variant="contained">
              <Accordion.Item value="slider">
                <Accordion.Control bg="indigo.2">
                  <Center>
                    <Text fw={700}>Slider</Text>
                  </Center>
                </Accordion.Control>
                <Accordion.Panel>Content</Accordion.Panel>
              </Accordion.Item>
              {/* Category List */}
              <Accordion.Item value="category-list">
                <Accordion.Control bg="indigo.2">
                  <Center>
                    <Text fw={700}>Category List</Text>
                  </Center>
                </Accordion.Control>
                <Accordion.Panel bg="gray.2">
                  <Box
                    mb="xs"
                    bg="#FFFFFF"
                    onClick={handleCategoryListClick}
                    style={{ cursor: "pointer" }}
                  >
                    <Flex
                      h={100}
                      justify="center"
                      align="center"
                      direction="row"
                      gap="sm"
                    >
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                        />
                        <Text size="xs">Electronics</Text>
                      </Flex>
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress"
                        />
                        <Text size="xs">Accessories</Text>
                      </Flex>
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528"
                        />
                        <Text size="xs">Clothing</Text>
                      </Flex>
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg"
                        />
                        <Text size="xs">Shoes</Text>
                      </Flex>
                      {iconCheckVisible && (
                      <Flex h={`100%`}>
                        <IconCheck />
                      </Flex>
                      )}
                    </Flex>
                  </Box>
                  <Box
                    bg="dark"
                    onClick={handleCategoryList2Click}
                    style={{ cursor: "pointer" }}
                  >
                    <Flex
                      h={100}
                      justify="center"
                      align="center"
                      direction="row"
                      gap="sm"
                    >
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                        />
                        <Text size="xs" c={"gray.0"}>
                          Electronics
                        </Text>
                      </Flex>
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress"
                        />
                        <Text size="xs" c={"gray.0"}>
                          Accessories
                        </Text>
                      </Flex>
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528"
                        />
                        <Text size="xs" c={"gray.0"}>
                          Clothing
                        </Text>
                      </Flex>
                      <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg"
                        />
                        <Text size="xs" c={"gray.0"}>
                          Shoes
                        </Text>
                      </Flex>
                      {iconCheckVisible2 && (
                        <Flex h={`100%`}>
                          <IconCheck color="#FFFFFF" />
                        </Flex>
                      )}
                    </Flex>
                  </Box>
                  <Box
                    bg="dark"
                    mt="xs"
                    onClick={handleCategoryList3Click}
                    style={{ cursor: "pointer" }}
                  >
                    <Flex
                      h={50}
                      justify="center"
                      align="center"
                      direction="row"
                      gap="sm"
                    >
                      <Flex
                        gap="sm"
                        justify="center"
                        align="center"
                        direction="row"
                      >
                        <Badge size="xs" color="blue">
                          All Product
                        </Badge>
                        <Badge size="xs" color="dark.4">
                          Electronics
                        </Badge>
                        <Badge size="xs" color="dark.4">
                          Clothing
                        </Badge>
                      </Flex>
                      {iconCheckVisible3 && (
                      <Flex h={`100%`}>
                        <IconCheck color="#FFFFFF" />
                      </Flex>
                      )}
                    </Flex>
                  </Box>

                  <Box
                    bg="#FFFFFF"
                    mt="xs"
                    onClick={handleCategoryList4Click}
                    style={{ cursor: "pointer" }}
                  >
                    <Flex
                      h={50}
                      justify="center"
                      align="center"
                      direction="row"
                      gap="sm"
                    >
                      <Flex
                        gap="sm"
                        justify="center"
                        align="center"
                        direction="row"
                      >
                        <Badge size="xs" color="blue">
                          Technology
                        </Badge>
                        <Badge size="xs" color="dark.4">
                          Fashion
                        </Badge>
                        <Badge size="xs" color="dark.4">
                          Sports
                        </Badge>
                        <Badge size="xs" color="dark.4">
                          Home
                        </Badge>
                      </Flex>
                      {iconCheckVisible4 && (
                      <Flex h={`100%`}>
                        <IconCheck />
                      </Flex>
                      )}
                    </Flex>
                  </Box>
                </Accordion.Panel>
              </Accordion.Item>
              {/* Category With Product */}
              <Accordion.Item value="category-with-product">
                <Accordion.Control bg="indigo.2">
                  <Center>
                    <Text fw={700}>Category With Product</Text>
                  </Center>
                </Accordion.Control>
                <Accordion.Panel>
                  <Box
                    bg="gray.2"
                    onClick={handleCategoryWithProductClick}
                    style={{ cursor: "pointer" }}
                  >
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                    >
                      <Text ml="xs">{categoryWithProductHeaderValue}</Text>
                      {showCheckbox && (
                        <Checkbox
                          checked={checkboxChecked}
                          onChange={(event) =>
                            setCheckboxChecked(event.target.checked)
                          }
                          mr="xs"
                        />
                      )}
                    </Flex>
                    <Flex
                      h={220}
                      gap="2px"
                      justify="flex-start"
