import { Box, Center, Stack, Image } from "@mantine/core";
import React from "react";

const ResponsivePhone = () => {
  return (
    <Stack>
      <Center>
        {/* <Box style={{ border: "1px solid #1b1c1b", borderRadius: "5px 5px 5px 5px" }}>Mobile</Box> */}
        {/* <Image
            maw={740}
            mx="auto"
            radius="md"
            src="https://media.istockphoto.com/id/1309147753/vector/realistic-mobile-phone-mockup-template-isolated-stock-vector.jpg?s=612x612&w=0&k=20&c=llG65plOEQMKpWq0e04XRls2mufZa3mhh-pEnz5iGbU="
            alt="Random image"
          /> */}
        <Box
          style={{
            border: "2px solid #1b1c1b",
            borderRadius: "8px",
            width: "200px", // Fixed width
            height: "400px", // Fixed height
            backgroundColor: "#ffffff",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
            position: "relative",
          }}
        >
          <div
            style={{
              width: "60px",
              height: "60px",
              backgroundColor: "#1b1c1b",
              borderRadius: "50%",
              position: "absolute",
              bottom: "20px",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Center style={{ color: "#ffffff", fontSize: "24px" }}>ðŸ“·</Center>
          </div>
          <Center>Mobile</Center>
        </Box>
      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
