import React from "react";
import { useDisclosure } from "@mantine/hooks";
import { AppShell, Burger, Group, Skeleton, Text, Menu, MenuItem, Center, Image } from "@mantine/core";
import Header from "./Header";
import AppBar from "./Global/AppBar";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();

  const headerStyles = {
    backgroundColor: "var(--mantine-color-blue-4)", // Set the background color to indigo
    borderRadius: "0.5rem", // Apply rounded corners
    padding: "0.5rem", // Add padding to the header
  };

  return (
    <AppShell
      header={{ height: 90 }}
      padding="md"
      // footer={{ height: 60 }}
      // navbar={{ width: 300, breakpoint: "sm", collapsed: { mobile: !opened } }}
      // aside={{
      //   width: 300,
      //   breakpoint: "md",
      //   collapsed: { desktop: false, mobile: true },
      // }}
    >
      <div style={{ backgroundColor: "blue" }} className="rounded-lg p-2">
        <AppShell.Header style={headerStyles}>
          <Group h="40%" px="md">
            <Text>Them-1</Text>
          </Group>
          <Group h="60%" px="md">
            <Header />
          </Group>

          {/* Burger */}
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
        </AppShell.Header>
      </div>
      {/* <AppShell.Navbar p="md">
        <AppBar />
