import React, { useState } from "react";
import {
  Box,
  Text,
  Grid,
  Center,
  Flex,
  Avatar,
  Checkbox,
  Accordion,
} from "@mantine/core";
import { IconX } from "@tabler/icons-react";
import ResponsivePhone from "../ResponsivePhone";

const categories = [
  {
    name: "Electronics",
    imageSrc:
      "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
  },
  {
    name: "Accessories",
    imageSrc:
      "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
  },
  {
    name: "Clothing",
    imageSrc:
      "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
  },
  {
    name: "Shoes",
    imageSrc:
      "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
  },
];

const Index = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedCategoryContent, setSelectedCategoryContent] = useState(null);

  const handleCategoryClick = (categoryName) => {
    setSelectedCategory(categoryName);

    // Create the category content to be displayed
    const categoryContent = (
      <Flex
        style={{
          flex: 1,
          background: "var(--mantine-color-gray-2)",
        }}
      >
        {/* Your category-specific content goes here */}
      </Flex>
    );

    setSelectedCategoryContent(categoryContent);
  };

  return (
    <Grid mt="xs" pb="xs">
      <Grid.Col span={4}>
        <Box
          bg="indigo.5"
          ml="xs"
          mr="xs"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            backgroundColor: "indigo.5",
            padding: "10px",
            borderRadius: "10px",
          }}
        >
          <Text
            fw={900}
            c={`#FFFFFF`}
            style={{
              flex: "1",
              textAlign: "center",
            }}
          >
            Layouts
          </Text>
          <IconX
            size={28}
            style={{
              borderRadius: "50%",
              background: "red",
              color: "white",
              alignSelf: "flex-end",
              cursor: "pointer",
            }}
          />
        </Box>
        <Accordion m="xs" pb={10} variant="contained">
          <Accordion.Item value="slider">
            <Accordion.Control bg="indigo.2">
              <Center>
                <Text fw={700}>Slider</Text>
              </Center>
            </Accordion.Control>
            <Accordion.Panel>
              <Flex style={{ overflowX: "auto" }}>
                {categories.map((category, index) => (
                  <Box
                    key={index}
                    bg="#FFFFFF"
                    ml="xs"
                    style={{ cursor: "pointer" }}
                    onClick={() => handleCategoryClick(category.name)}
                  >
                    <Flex
                      //   h="100px"
                      justify="center"
                      align="center"
                      direction="column"
                    >
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src={category.imageSrc}
                      />
                      <Text size="xs">{category.name}</Text>
                    </Flex>
                  </Box>
                ))}
                <Checkbox defaultChecked />
              </Flex>
            </Accordion.Panel>
          </Accordion.Item>
        </Accordion>
      </Grid.Col>
      <Grid.Col span={4}>
        <ResponsivePhone selectedCategory={selectedCategory} categoryContent={selectedCategoryContent} />
      </Grid.Col>
      <Grid.Col span={4}>3</Grid.Col>
    </Grid>
  );
};

export default Index;
