import React, {useState} from "react";
import {
  Box,
  Stack,

  Text,
  Accordion,
} from "@mantine/core";

import {
  IconX,
  IconPlus
} from "@tabler/icons-react";
import CustomizeCategoryList1 from "./CustomizeCategoryList1";

const Customize = ({categoryListBoxVisible}) => {
  const [boxValue, setBoxValue] = useState(1);
  const [paddingValue, setPaddingValue] = useState(1);
  const [marginValue, setMarginValue] = useState(1);

  const handleRadiusChange = (newValue) => {
    setBoxValue(newValue);
  };

  const handleRadiusIncrement = () => {
    const newValue = boxValue + 1;
    if (newValue <= 100) {
      setBoxValue(newValue);
    }
  };

  const handlePaddingChange = (newValue) => {
    setPaddingValue(newValue);
  };

  const handlePaddingIncrement = () => {
    const newValue = paddingValue + 1;
    if (newValue <= 100) {
      setPaddingValue(newValue);
    }
  };

  const handleMarginChange = (newValue) => {
    setMarginValue(newValue);
  };

  const handleMarginIncrement = () => {
    const newValue = marginValue + 1;
    if (newValue <= 100) {
      setMarginValue(newValue);
    }
  };

  const handleCategoryWithProductRadiusChange = (newValue) => {
    setCategoryWithProductRadiusValue(newValue);
  };

  const handleCategoryWithProductRadiusIncrement = () => {
    const newValue = categoryWithProductRadiusValue + 1;
    if (newValue <= 100) {
      setCategoryWithProductRadiusValue(newValue);
    }
  };

  const handleProductNameTextSizeChange = (newValue) => {
    setProductNameTextSizeValue(newValue);
  };

  const handleProductNameTextSizeIncrement = () => {
    const newValue = productNameTextSizeValue + 1;
    if (newValue <= 20) {
      setProductNameTextSizeValue(newValue);
    }
  };
  return (
    <>
    {categoryListBoxVisible &&(
    <Box m="xs" h={`fit-content`} bg="gray.2" style={{ borderRadius: "10px" }}>
      
      <Box
        bg="indigo.5"
        ml="xs"
        mr="xs"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "indigo.5",
          padding: "10px",
          borderRadius: "10px",
        }}
      >
        <Text
          fw={900}
          c={`#FFFFFF`}
          style={{
            flex: "1",
            textAlign: "center",
          }}
        >
          Customize
        </Text>
        <IconX
          size={28}
          style={{
            borderRadius: "50%",
            background: "red",
            color: "white",
            alignSelf: "flex-end",
          }}
        />
      </Box>
      <Accordion
        m="xs"
        variant="contained"
        defaultValue="category-item"
        pb={10}
      >
        <Accordion.Item value="category-item">
          <Accordion.Control bg="#FFFFFF">
            <Text>Category Item</Text>
          </Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            <CustomizeCategoryList1 />
          </Accordion.Panel>
        </Accordion.Item>
        {/* settings */}
      <Accordion.Item value="settings" mt="xs">
        <Accordion.Control bg="#FFFFFF">
          <Text>Settings</Text>
        </Accordion.Control>
        <Accordion.Panel bg="#FFFFFF">
          <Stack gap={0}>
            {/* Box Radius */}
            <Text>Box Radius</Text>
            <Flex
              mih={50}
              bg="white"
              gap="xs"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                border: "1px solid #1b1c1b",
                borderRadius: "5px",
              }}
            >
              <Input
                type="number"
                size="xs"
                value={boxValue}
                w={45}
                readOnly
                style={{ borderRadius: "20px" }}
              />
              <Slider
                my="xs"
                value={boxValue}
                onChange={handleRadiusChange}
                min={0}
                max={100}
                w={230}
                label={null}
              />
              <IconPlus
                onClick={handleRadiusIncrement}
                style={{
                  border: "1px solid #000",
                  borderRadius: "50%",
                  cursor: "pointer",
                }}
              />
            </Flex>
            {/* padding */}
            <Text>Padding</Text>
            <Flex
              mih={50}
              bg="white"
              gap="xs"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                border: "1px solid #1b1c1b",
                borderRadius: "5px",
              }}
            >
              <Input
                type="number"
                size="xs"
                value={paddingValue}
                w={45}
                readOnly
                style={{ borderRadius: "20px" }}
              />
              <Slider
                my="xs"
                value={paddingValue}
                onChange={handlePaddingChange}
                min={0}
                max={100}
                w={230}
                label={null}
              />
              <IconPlus
                onClick={handlePaddingIncrement}
                style={{
                  border: "1px solid #000",
                  borderRadius: "50%",
                  cursor: "pointer",
                }}
              />
            </Flex>
            {/* Margin */}
            <Text>Margin</Text>
            <Flex
              mih={50}
              bg="white"
              gap="xs"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                border: "1px solid #1b1c1b",
                borderRadius: "5px",
              }}
            >
              <Input
                type="number"
                size="xs"
                value={marginValue}
                w={45}
                readOnly
                style={{ borderRadius: "20px" }}
              />
              <Slider
                my="xs"
                value={marginValue}
                onChange={handleMarginChange}
                min={0}
                max={100}
                w={230}
                label={null}
              />
              <IconPlus
                onClick={handleMarginIncrement}
                style={{
                  border: "1px solid #000",
                  borderRadius: "50%",
                  cursor: "pointer",
                }}
              />
            </Flex>
          </Stack>
        </Accordion.Panel>
      </Accordion.Item>
      </Accordion>
    </Box>
    )}
    </>
  );
};

export default Customize;
