import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Button,
  ColorPicker,
} from "@mantine/core";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";
import {
  IconCircleXFilled,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
} from "@tabler/icons-react";
const ImageTest = () => {
  const [value, setValue] = useState(1);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };
  return (
    <Box bg="gray.3" style={{ maxHeight: "78vh", overflowY: "scroll" }}>
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Image
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Image</Text>
        <Dropzone
          bg="white"
          h="5rem"
          w="22rem"
          ml="xs"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Text mt="1.5rem" ta="center">
            Select Image
          </Text>
        </Dropzone>
      </Stack>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Hight</Text>
        <Flex
          mih={50}
          bg="white"
          // my="xs"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          {/* <Input
                type="number"
                value={0}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={25}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={50}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={75}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={100}
                readOnly
                style={{ width: "50px" }}
              /> */}
          <Slider
            value={value}
            onChange={handleChange}
            min={0}
            max={100}
            labelAlwaysOn
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Width</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={value}
            onChange={handleChange}
            min={0}
            max={100}
            labelAlwaysOn
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Alignment</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Radio.Group>
            <Group>
              <Radio
                value="center"
                label={<IconAlignCenter style={{ border: "1px solid #ccc" }} />}
              />
              <Radio
                value="left"
                label={
                  <IconAlignJustified style={{ border: "1px solid #ccc" }} />
                }
              />
              <Radio
                value="right"
                label={<IconAlignRight style={{ border: "1px solid #ccc" }} />}
              />
            </Group>
          </Radio.Group>
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Alignment</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          
        </Flex>
      </Stack>
      <Accordion m="xs" variant="contained">
        <Accordion.Item value="shape-type">
          <Accordion.Control>Hight</Accordion.Control>
          <Accordion.Panel>
            <Flex
              mih={50}
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              {/* <Input
                type="number"
                value={0}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={25}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={50}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={75}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={100}
                readOnly
                style={{ width: "50px" }}
              /> */}
              <Slider
                value={value}
                onChange={handleChange}
                min={0}
                max={100}
                labelAlwaysOn
                style={{ width: "300px" }}
              />
            </Flex>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="shape-type">
          <Accordion.Control>Width</Accordion.Control>
          <Accordion.Panel>
            <Flex
              mih={50}
              //   bg="rgba(0, 0, 0, .3)"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              {/* <Input
                type="number"
                value={0}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={25}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={50}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={75}
                readOnly
                style={{ width: "40px" }}
              />
              <Input
                type="number"
                value={100}
                readOnly
                style={{ width: "50px" }}
              /> */}
              <Slider
                value={value}
                onChange={handleChange}
                min={0}
                max={100}
                labelAlwaysOn
                style={{ width: "300px" }}
              />
            </Flex>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="weight">
          <Accordion.Control>Alignment</Accordion.Control>
          <Accordion.Panel>
            <Flex
              mih={50}
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              <Radio.Group>
                <Group>
                  <Radio
                    value="center"
                    label={
                      <IconAlignCenter style={{ border: "1px solid #ccc" }} />
                    }
                  />
                  <Radio
                    value="left"
                    label={
                      <IconAlignJustified
                        style={{ border: "1px solid #ccc" }}
                      />
                    }
                  />
                  <Radio
                    value="right"
                    label={
                      <IconAlignRight style={{ border: "1px solid #ccc" }} />
                    }
                  />
                </Group>
              </Radio.Group>
              {/* <Box style={{ border: "1px solid #ccc" }}>
                <IconPhoto />
              </Box>
              <Box style={{ border: "1px solid #ccc" }}>
                <IconPhoto />
              </Box>
              <Box style={{ border: "1px solid #ccc" }}>
                <IconPhoto />
              </Box> */}
            </Flex>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="shape-type">
          <Accordion.Control>Boxfit</Accordion.Control>
          <Accordion.Panel>
            <Flex
              mih={50}
              //   bg="rgba(0, 0, 0, .3)"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              <Radio.Group>
                <Group>
                  <Radio value="cover" label="Cover" />
                  <Radio value="contain" label="Contain" />
                  <Radio value="fill" label="Fill" />
                </Group>
              </Radio.Group>
            </Flex>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Background Color</Text>
        {/* <ColorInput
          ml="xs"
          mr="xs"
          placeholder="Choose Background Colors"
          fullWidth
          style={{ display: "inline-block" }}
        /> */}

        <ColorPicker fullWidth size="lg" format="rgba" />
      </Stack>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="shape-type">
          <Accordion.Control>Back Button Radious</Accordion.Control>
          <Accordion.Panel>
            {/* <Flex
              mih={50}
              //   bg="rgba(0, 0, 0, .3)"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              <Text>Top Left</Text>
              <Input
                type="number"
                value={value}
                readOnly
                style={{ width: "50px" }}
              />
              <Slider
                my="xs"
                value={value}
                onChange={handleChange}
                min={0}
                max={100}
                style={{ width: "80px" }}
              />
              <Button
                ml="xs"
                onClick={handleIncrement}
                variant="outline"
                radius="xl"
              >
                +
              </Button>
            </Flex> */}
            <Stack
              mih={50}
              gap="md"
              m="xs"
              display="flex"
              justify="center"
              align="flex-start"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc", borderRadius: "5px" }}
            >
              <Flex
                mih={50}
                gap="md"
                ml="xs"
                justify="center"
                align="flex-start"
                direction="column"
                wrap="wrap"
              >
                <Text mt="xs">Left</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Right</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Top</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Bottom</Text>

                <Slider
                  labelAlwaysOn
                  mb="xs"
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
              </Flex>
            </Stack>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="shape-type">
          <Accordion.Control>Padding</Accordion.Control>
          <Accordion.Panel>
            {/* <Flex
              mih={50}
              //   bg="rgba(0, 0, 0, .3)"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              <Text>Left</Text>
              <Input
                type="number"
                value={value}
                readOnly
                style={{ width: "50px" }}
              />
              <Slider
                my="xs"
                value={value}
                onChange={handleChange}
                min={0}
                max={100}
                style={{ width: "100px" }}
              />
              <Button
                ml="xs"
                onClick={handleIncrement}
                variant="outline"
                radius="xl"
              >
                +
              </Button>
            </Flex> */}
            <Stack
              mih={50}
              gap="md"
              m="xs"
              display="flex"
              justify="center"
              align="flex-start"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc", borderRadius: "5px" }}
            >
              <Flex
                mih={50}
                gap="md"
                ml="xs"
                justify="center"
                align="flex-start"
                direction="column"
                wrap="wrap"
              >
                <Text mt="xs">Left</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Right</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Top</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Bottom</Text>

                <Slider
                  labelAlwaysOn
                  mb="xs"
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
              </Flex>
            </Stack>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="shape-type">
          <Accordion.Control>Margin</Accordion.Control>
          <Accordion.Panel>
            {/* <Flex
              mih={50}
              //   bg="rgba(0, 0, 0, .3)"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc" }}
            >
              <Input
                type="number"
                value={value}
                readOnly
                style={{ width: "50px" }}
              />
              <Slider
                my="xs"
                value={value}
                onChange={handleChange}
                min={0}
                max={100}
                style={{ width: "150px" }}
              />
              <Button
                ml="xs"
                onClick={handleIncrement}
                variant="outline"
                radius="xl"
              >
                +
              </Button>
            </Flex> */}
            <Stack
              mih={50}
              gap="md"
              m="xs"
              display="flex"
              justify="center"
              align="flex-start"
              direction="row"
              wrap="wrap"
              style={{ border: "1px solid #ccc", borderRadius: "5px" }}
            >
              <Flex
                mih={50}
                gap="md"
                ml="xs"
                justify="center"
                align="flex-start"
                direction="column"
                wrap="wrap"
              >
                <Text mt="xs">Left</Text>

                <Slider
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  labelAlwaysOn
                  style={{ width: "300px" }}
                />
                <Text>Right</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Top</Text>

                <Slider
                  labelAlwaysOn
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
                <Text>Bottom</Text>

                <Slider
                  labelAlwaysOn
                  mb="xs"
                  value={value}
                  onChange={handleChange}
                  min={0}
                  max={100}
                  style={{ width: "300px" }}
                />
              </Flex>
            </Stack>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </Box>
  );
};

export default ImageTest;
