import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";

import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";

const CustomizeCategoryList1 = () => {
  /* Dropzone */
  const [files, setFiles] = useState([]);

  const previews = files.map((file, index) => {
    const imageUrl = URL.createObjectURL(file);
    return (
      <Image
        key={index}
        src={imageUrl}
        onLoad={() => URL.revokeObjectURL(imageUrl)}
      />
    );
  });

  const accordionItems = [
    {
      value: "electronics",
      label: "Electronics",
      level: "electronic_level",
    },
    {
      value: "accessories",
      label: "Accessories",
      level: "accessories_level",
    },
    {
      value: "clothing",
      label: "Clothing",
      level: "clothing_level",
    },
    {
      value: "shoes",
      label: "Shoes",
      level: "shoes_level",
    },
  ];

  const [categoryHeader, setCategoryHeader] = useState({
    electronic_level: "Electronics",
    accessories_level: "Accessories",
    clothing_level: "Clothing",
    shoes_level: "Shoes",
  });

  const updateProperty = (property, value) => {
    setCategoryHeader({ ...categoryHeader, [property]: value });
  };

  const onLevelChange = (level, text) => {
    updateProperty(level, text);
  };

  return (
    <Accordion m="xs" variant="contained">
      {accordionItems.map((item) => (
        <Accordion.Item key={item.value} value={item.value}>
          <Accordion.Control bg="gray.2" direction="row">
            <Flex justify="flex-start" align="center" direction="row" gap="md">
              <Checkbox defaultChecked />
              <Text>{item.label}</Text>
            </Flex>
          </Accordion.Control>
          <Accordion.Panel>
            <Dropzone
              accept={IMAGE_MIME_TYPE}
              onDrop={setFiles}
              bg="white"
              h="10rem"
              style={{
                position: "relative",
                border: "1px solid #1b1c1b",
                borderRadius: "5px",
              }}
            >
              {previews.length === 0 ? (
                <Text
                  style={{
                    position: "absolute",
                    top: "50%",
                    left: "50%",
                    transform: "translate(-50%, -50%)",
                  }}
                >
                  Drop images here
                </Text>
              ) : null}
              {previews}
            </Dropzone>
            <Flex direction="column">
              <Text mt="xs">Background Color</Text>
              <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
            </Flex>
            {item.level && (
              <Flex direction="column">
                <Text mt="xs">Level</Text>
                <Input
                  placeholder="Input component"
                  value={categoryHeader[item.level]}
                  onChange={(event) => {
                    onLevelChange(item.level, event.target.value);
                  }}
                />
              </Flex>
            )}
          </Accordion.Panel>
        </Accordion.Item>
      ))}
    </Accordion>
  );
};

export default CustomizeCategoryList1;
