import React, {use} from "react";
import { Box, Text, Flex, Accordion, Input } from "@mantine/core";
import { IconX } from "@tabler/icons-react";
import CustomizeCategory from "./CustomizeCategory";

const CategoryWithProductCustomize = ({
  categoryWithProductVisible,
  setCategoryWithProductHeaderValue,
  categoryWithProductHeaderValue,
}) => {
  const [isCustomizeVisible, setIsCustomizeVisible] = useState(categoryWithProductVisible);

  const handleHeaderChange = (event) => {
    setCategoryWithProductHeaderValue(event.target.value);
  };

  const toggleCustomizeVisibility = () => {
    setIsCustomizeVisible(!isCustomizeVisible);
  };

  return (
    <>
      {isCustomizeVisible  && (
        <Box
          m="xs"
          h={`fit-content`}
          bg="gray.2"
          mb="xs"
          style={{ borderRadius: "10px" }}
        >
          <Box
            bg="indigo.5"
            ml="xs"
            mr="xs"
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              backgroundColor: "indigo.5",
              padding: "10px",
              borderRadius: "10px",
            }}
          >
            <Text
              fw={900}
              c={`#FFFFFF`}
              style={{
                flex: "1",
                textAlign: "center",
              }}
            >
              Customize
            </Text>
            <IconX
              size={28}
              style={{
                borderRadius: "50%",
                background: "red",
                color: "white",
                alignSelf: "flex-end",
              }}
              onClick={toggleCustomizeVisibility}
            />
          </Box>

          {/* Header Input */}
          <Flex
            ml="xs"
            mr="xs"
            mt="xs"
            mih={50}
            bg="#FFFFFF"
            gap="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
            style={{ borderRadius: "10px" }}
          >
            <Text ml="xs">Header</Text>
            <Input
              size="xs"
              ml="xs"
              mr="xs"
              mb="xs"
              w={`95%`}
              placeholder="Input component"
              value={categoryWithProductHeaderValue}
              onChange={handleHeaderChange}
            />
          </Flex>

          <Accordion m="xs" pb={10} variant="contained">
            {/* Type */}
            <CustomizeCategory />
          </Accordion>
        </Box>
      )}
    </>
  );
};

export default CategoryWithProductCustomize;
