import React from "react";
import { Flex, Stack, Text, Container, Box, Accordion } from "@mantine/core";
import {
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";
const Drawer = () => {
  return (
    <Box bg="gray.3">
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Drawer Icon
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Accordion.Item my="xs" value="shadow">
        <Accordion.Control>Shadow</Accordion.Control>
        <Accordion.Panel>
          <Stack bg="gray.0">
            <Center>
              
            </Center>
          </Stack>
        </Accordion.Panel>
      </Accordion.Item>
    </Box>
  );
};

export default Drawer;
