import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Slider,
  ColorPicker,
  Avatar,
  Group,
  Accordion,
  rem,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconHome2,
  IconUser,
  IconSearch,
  IconMessageCircle,
  IconShoppingBag,
  IconBookmark,
  IconPhoto,
  IconPrinter,
  IconCameraSelfie,
} from "@tabler/icons-react";

const AccordionBar = () => {
  const [selectedIcon, setSelectedIcon] = useState(null);
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);
  const [selectedRedIcon, setSelectedRedIcon] = useState(null);

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const handleRedIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedRedIcon(index);
  };

  const icons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconSearch", component: <IconSearch /> },
    { name: "IconShoppingBag", component: <IconShoppingBag /> },
    { name: "IconUser", component: <IconUser /> },
  ];
  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];
  const redicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];
  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          AppBar and Navbar Accordion
        </Text>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "1rem",
          }}
        >
          <IconCircleXFilled />
        </div>
      </Flex>

      <Accordion m="xs" variant="contained">
        <Accordion.Item value="appbar">
          <Accordion.Control>App Bar</Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="navbar">
          <Accordion.Control>Nav Bar</Accordion.Control>
          <Accordion.Panel>
            <Stack m="xs" bg="indigo.2">
              <Flex
                mih={50}
                bg="white"
                m="xs"
                ml="xs"
                mr="xs"
                gap="md"
                justify="center"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "25px" }}
              >
                {icons.map((icon, index) => (
                  <ActionIcon
                    radius="xl"
                    key={index}
                    variant={selectedIcon === index ? "outline" : "default"}
                    aria-label={`Icon ${icon.component}`}
                    onClick={() => handleIconClick(index, icon)}
                  >
                    {/* <Avatar>{icon.component}</Avatar> */}
                    {icon.component}
                  </ActionIcon>
                ))}
              </Flex>
            </Stack>

            <Flex
              mih={50}
              bg="dark.9"
            //   ml="xl"
            //   mr="xl"
              justify="space-around"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                border: "1px solid #1b1c1b",
                borderRadius: "0 0 25px 25px",
                padding: "10px",
              }}
            >
              {blackicons.map((blackicon, index) => (
                <ActionIcon
                  bg="black"
                  radius="md"
                  key={index}
                  variant={selectedBlackIcon === index ? "transparent" : ""}
                  aria-label={`Icon ${blackicon.component}`}
                  onClick={() => handleBlackIconClick(index, blackicon)}
                >
                  {blackicon.component}
                </ActionIcon>
              ))}
            </Flex>

            <Flex
              mih={50}
              bg="white"
              m="xs"
              justify="space-around"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                border: "1px solid #1b1c1b",
                borderRadius: "0 0 25px 25px",
              }}
            >
              {redicons.map((redicon, index) => (
                <ActionIcon
                  color="red"
                  radius="md"
                  key={index}
                  variant={selectedRedIcon === index ? "" : "transparent"}
                  aria-label={`Icon ${redicon.component}`}
                  onClick={() => handleRedIconClick(index, redicon)}
                >
                  {redicon.component}
                </ActionIcon>
              ))}
            </Flex>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </Stack>
  );
};

export default AccordionBar;
