import {
  Avatar,
  Container,
  Flex,
  Stack,
  Autocomplete,
  Box,
  Button,
} from "@mantine/core";
import React, { useState } from "react";
import {
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";
const AppBar = () => {
  const [isSelected, setIsSelected] = useState(false);

  const handleContainerClick = () => {
    setIsSelected(!isSelected);
  };

  return (
    <Flex
      mih={50}
      bg="rgba(0, 0, 0, .3)"
      gap="md"
      justify="center"
      align="center"
      direction="column"
      wrap="wrap"
      radius="xl"
    >
      <Stack style={{ width: '100%' }} bg="var(--mantine-color-blue-light)"></Stack>
      <Button mb="xs">Button 3</Button>
    </Flex>
  );
};

export default AppBar;
