import React from "react";
import { useDisclosure } from "@mantine/hooks";
import {
  AppShell,
  Burger,
  Group,
  Center,
  Image,
  Text,
} from "@mantine/core";
import {
  IconAlignJustified,
  IconCircleXFilled,
} from "@tabler/icons-react";
import NavbarMinimal from "./NavbarMinimal";
import Demo from "./Demo";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();
  return (
    <AppShell
      header={{ height: 50 }}
      navbar={{ width: 80, breakpoint: "sm", collapsed: { mobile: !opened } }}
      aside={{
        width: 300,
        breakpoint: "md",
        collapsed: { desktop: false, mobile: true },
      }}
      padding="md"
    >
      <AppShell.Header>
  <Group h="100%" px="md" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
      <div style={{ display: 'flex', alignItems: 'center', marginLeft: '8px' }}>
        <IconAlignJustified />
        <Text color="#ff5733" style={{ marginLeft: '4px' }}>Theme-1</Text>
      </div>
    </div>
    <div className="justyfi-end">
      <IconCircleXFilled />
    </div>
  </Group>
</AppShell.Header>

      <AppShell.Navbar p="md">
          {/* <NavbarMinimal /> */}
      </AppShell.Navbar>
      <AppShell.Main>
      <Center>
        <Image
          maw={740}
          mx="auto"
          radius="md"
          src="https://media.istockphoto.com/id/1309147753/vector/realistic-mobile-phone-mockup-template-isolated-stock-vector.jpg?s=612x612&w=0&k=20&c=llG65plOEQMKpWq0e04XRls2mufZa3mhh-pEnz5iGbU="
          alt="Random image"
        />
      </Center>
      </AppShell.Main>
      <AppShell.Aside p="md">
        <Demo />
      </AppShell.Aside>
    </AppShell>
  );
};

export default AppShellDemo;
