//       { link: '/faq', label: 'FAQ' },
//       { link: '/demo', label: 'Book a demo' },
//       { link: '/forums', label: 'Forums' },
//     ],
//   },
//   {
//     link: '#5',
//     label: 'Support',
//     links: [
//       { link: '/faq', label: 'FAQ' },
//       { link: '/demo', label: 'Book a demo' },
//       { link: '/forums', label: 'Forums' },
//     ],
//   },
//   {
//     link: '#6',
//     label: 'Support',
//     links: [
//       { link: '/faq', label: 'FAQ' },
//       { link: '/demo', label: 'Book a demo' },
//       { link: '/forums', label: 'Forums' },
//     ],
