import React, { useState } from "react";
import {
  Button,
  SegmentedControl,
  Drawer,
  Box,
  Flex,
  Modal,
} from "@mantine/core";
import {
  IconShoppingCart,
  IconLicense,
  IconMessage2,
  IconMessages,
  IconApps,
  IconSettings,
  IconLayoutSidebarLeftExpand,
  IconUsers,
  IconFileAnalytics,
  IconLayoutBottombarExpand,
  IconReceiptRefund,
  IconFlag3,
  IconFlag,
} from "@tabler/icons-react";
import { useDisclosure } from "@mantine/hooks";
import classes from "./NavbarSegmented.module.css";
import Appbar from "../Global/AppBar";
import Navbar from "../Global/Navbar";
import Drawer2 from "../Global/Drawer";
import Index from "../AppBar/Index";

const tabs = {
  account: [
    { link: "", label: "Appbar", icon: IconApps },
    { link: "", label: "Navbar", icon: IconLayoutBottombarExpand },
    { link: "", label: "Drawer", icon: IconLayoutSidebarLeftExpand },
  ],
  general: [
    { link: "", label: "Orders", icon: IconShoppingCart },
    { link: "", label: "Receipts", icon: IconLicense },
    { link: "", label: "Reviews", icon: IconMessage2 },
    { link: "", label: "Messages", icon: IconMessages },
    { link: "", label: "Customers", icon: IconUsers },
    { link: "", label: "Refunds", icon: IconReceiptRefund },
    { link: "", label: "Files", icon: IconFileAnalytics },
  ],
};

const NavbarSegmented = ({
  selectAppBar,
  setSelectAppBar,
  selectAppBar2,
  setSelectAppBar2,
  selectAppBar3,
  setSelectAppBar3,
  onItemClick,
  selectNavBar,
  setSelectNavBar,
  selectNavBar2,
  setSelectNavBar2,
  selectNavBar3,
  setSelectNavBar3,
  onNavBarItemClick,
}) => {
  const [section, setSection] = useState("account");
  const [active, setActive] = useState("Appbar");
  const [isAppbarDrawerOpen, setIsAppbarDrawerOpen] = useState(false);
  const [isNavbarDrawerOpen, setIsNavbarDrawerOpen] = useState(false);
  const [isDrawerDrawerOpen, setIsDrawerDrawerOpen] = useState(false);
  const [isSettingsDrawerOpen, setIsSettingsDrawerOpen] = useState(false);
  const [isPageDrawerOpen, setIsPageDrawerOpen] = useState(false);

  const [opened, { open, close }] = useDisclosure(false);

  const links = tabs[section].map((item) => (
    <a
      className={classes.link}
      data-active={item.label === active || undefined}
      href={item.link}
      key={item.label}
      onClick={(event) => {
        event.preventDefault();
        setActive(item.label);

        if (item.label === "Appbar") {
          setIsAppbarDrawerOpen(true);
        } else if (item.label === "Navbar") {
          setIsNavbarDrawerOpen(true);
        } else if (item.label === "Drawer") {
          setIsDrawerDrawerOpen(true);
        }
      }}
    >
      <item.icon className={classes.linkIcon} stroke={1.5} />
      <span>{item.label}</span>
    </a>
  ));

  const closeAppbarDrawer = () => {
    setIsAppbarDrawerOpen(false);
  };

  const closeNavbarDrawer = () => {
    setIsNavbarDrawerOpen(false);
  };

  const closeDrawerDrawer = () => {
    setIsDrawerDrawerOpen(false);
  };

  // Conditionally render the footer based on the selected section
  const footer =
    section === "account" ? (
      <div className={classes.footer}>
        <a
          href="#"
          style={{
            display: "flex",
            marginRight: "10px",
            justifyContent: "space-between",
          }}
          onClick={(event) => event.preventDefault()}
        >
          <Flex mt="4px">
            <IconSettings
              className={classes.linkIcon}
              stroke={1.5}
              onClick={() => setIsSettingsDrawerOpen(true)}
            />
            <IconFlag3
              className={classes.linkIcon}
              stroke={1.5}
              onClick={() => setIsPageDrawerOpen(true)}
            />
            <IconFlag
              className={classes.linkIcon}
              stroke={1.5}
              onClick={open}
            />
          </Flex>
          <Button>Save</Button>
        </a>
      </div>
    ) : null;

  return (
    <nav className={classes.navbar}>
      <div>
        <SegmentedControl
          value={section}
          onChange={(value) => setSection(value)}
          fullWidth
          color="cyan"
          transitionDuration={500}
          transitionTimingFunction="linear"
          data={[
            { label: "Global", value: "account" },
            { label: "Component", value: "general" },
          ]}
        />
      </div>

      <div className={classes.navbarMain}>{links}</div>

      {footer}

      {/* Settings Drawer */}
      <Drawer
        title="Settings Drawer"
        opened={isSettingsDrawerOpen}
        onClose={() => setIsSettingsDrawerOpen(false)}
        position="left"
        size={300}
        transitionProps={{
          transition: "fade",
          duration: 2000,
          timingFunction: "linear",
        }}
      >
        <div>
          <h3>Settings Drawer Content</h3>
        </div>
      </Drawer>

      {/* Page Drawer */}
      <Drawer
        title="Page Drawer"
        opened={isPageDrawerOpen}
        onClose={() => setIsPageDrawerOpen(false)}
        position="left"
        size={300}
        transitionProps={{
          transition: "fade",
          duration: 2000,
          timingFunction: "linear",
        }}
      >
        <div>
          <h3>Page Drawer Content</h3>
        </div>
      </Drawer>

      {/* Page Modal */}
      <Modal
        opened={opened}
        onClose={close}
        title="This is a fullscreen modal"
        size={320}
        radius={0}
        transitionProps={{ transition: "fade", duration: 200 }}
      >
        {/* Modal content */}
        <Text>dsdadad</Text>
      </Modal>

      {/* AppBar Drawer*/}
      <Drawer
        title="App Bar Drawer"
        opened={isAppbarDrawerOpen}
        onClose={closeAppbarDrawer}
        position="left"
        size={500}
        transitionProps={{
          transition: "fade",
          duration: 2000,
          timingFunction: "linear",
        }}
      >
        {/* <Index
          Item={selectAppBar}
          setItem={setSelectAppBar}
          Item2={selectAppBar2}
          setItem2={setSelectAppBar2}
          appBar={onItemClick}
        /> */}
        <Appbar
          Item={selectAppBar}
          setItem={setSelectAppBar}
          Item2={selectAppBar2}
          setItem2={setSelectAppBar2}
          Item3={selectAppBar3}
          setItem3={setSelectAppBar3}
          appBar={onItemClick}
        />
      </Drawer>

      {/* New Navbar Drawer */}
      <Drawer
        title="Navbar Drawer"
        opened={isNavbarDrawerOpen}
        onClose={closeNavbarDrawer}
        position="left"
        size={500}
        transitionProps={{
          transition: "fade",
          duration: 2000,
          timingFunction: "linear",
        }}
      >
        <Navbar
          Item={selectNavBar}
          SetItem={setSelectNavBar}
          Item2={selectNavBar2}
          setItem2={setSelectNavBar2}
          Item3={selectNavBar3}
          setItem3={setSelectNavBar3}
          navBar={onNavBarItemClick}
        />
      </Drawer>

      {/* Drawer */}
      <Drawer
        title="Drawer Drawer"
        opened={isDrawerDrawerOpen}
        onClose={closeDrawerDrawer}
        position="left"
        size={300}
        transitionProps={{
          transition: "fade",
          duration: 2000,
          timingFunction: "linear",
        }}
      >
        <Drawer2 />
      </Drawer>
    </nav>
  );
};

export default NavbarSegmented;
