import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Divider,
  Button,
  Checkbox,
  ColorPicker,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignLeft,
  IconAlignCenter,
  IconAlignJustified,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
  IconCirclePlus,
} from "@tabler/icons-react";

