import React, { useState } from "react";
import { Box, Center, Grid, Stack, Text } from "@mantine/core";

const Layout = () => {
  const [selectedItem, setSelectedItem] = useState(null);

  // Function to handle item selection
  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  return (
    <Box bg="gray.4" h={200} style={{ borderRadius: "10px" }}>
      <Stack spacing={0}>
        <Box bg="lime.3" style={{ borderRadius: "10px 10px 0 0" }}>
          <Center>
            <Text>{selectedItem ? selectedItem : "Select an item"}</Text>
          </Center>
        </Box>
        <Grid ml="xs" mr="xs">
          <Grid.Col span={4}>
            <Box
              bg="white"
              h={60}
              style={{ border: "1px solid #000000" }}
              onClick={() => handleItemClick("Item 1")}
            >
              Item 1
            </Box>
          </Grid.Col>
          <Grid.Col span={4}>
            <Box
              bg="white"
              h={60}
              style={{ border: "1px solid #000000" }}
              onClick={() => handleItemClick("Item 2")}
            >
              Item 2
            </Box>
          </Grid.Col>
          <Grid.Col span={4}>
            <Box
              bg="white"
              h={60}
              style={{ border: "1px solid #000000" }}
              onClick={() => handleItemClick("Item 3")}
            >
              Item 3
            </Box>
          </Grid.Col>
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout;
