import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
} from "@mantine/core";

const CategoryList = ({
  setBoxVisible,
  setBoxVisible2,
  setBoxVisible3,
  setBoxVisible4,
}) => {
  const [iconCheckVisibles, setIconCheckVisibles] = useState([false, false, false, false]);

  const handleCategoryWithProductClick = (index) => {
    const newIconCheckVisibles = Array(4).fill(false);
    newIconCheckVisibles[index] = true;

    const setBoxVisibles = [setBoxVisible, setBoxVisible2, setBoxVisible3, setBoxVisible4];
    const setIconCheckFns = setBoxVisibles.map((setBoxVisibleFn, i) => {
      if (i === index) {
        return () => setBoxVisibleFn(true);
      }
      return () => setBoxVisibleFn(false);
    });

    setIconCheckVisibles(newIconCheckVisibles);

    setIconCheckFns[index]();
  };

  const categories = [
    {
      label: "Electronics",
      images: [
        "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
        "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
        "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
        "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
      ],
    },
    {
      label: "Accessories",
      images: [
        "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
        "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit",
        "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
        "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
      ],
    },
    {
      label: "Clothing",
      images: [
        "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
        "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit",
        "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
        "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
      ],
    },
    {
      label: "Shoes",
      images: [
        "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
        "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit",
        "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
        "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
      ],
    },
  ];

  const accordionItems = categories.map((category, index) => (
    <Accordion.Item key={index} value={`category-list-${index}`}>
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>{category.label}</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel bg="gray.2">
        {category.images.map((image, imageIndex) => (
          <Box
            key={imageIndex}
            mb="xs"
            bg="#FFFFFF"
            style={{ cursor: "pointer" }}
            onClick={() => handleCategoryWithProductClick(index)}
          >
            <Flex
              h={{ base: "auto", md: 100 }}
              justify="center"
              align="center"
              direction={{ base: "column", md: "row" }}
              gap="sm"
            >
              <Flex justify="center" align="center" direction="column">
                <Avatar
                  variant="filled"
                  radius="sm"
                  size="lg"
                  color="yellow"
                  src={image}
                />
                <Text size="xs">{category.label}</Text>
              </Flex>
              {iconCheckVisibles[index] && (
                <Flex h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  ));

  return <Accordion m="xs">{accordionItems}</Accordion>;
};

export default CategoryList;
