import React from "react";
import { useState } from "react";
import { Container, Group, Burger,Center } from "@mantine/core";
import { useDisclosure } from "@mantine/hooks";
import classes from "./HeaderMenu.module.css";

import {
    IconHome2,
    IconWorld,
    IconCategory,
    IconDatabase,
    IconGardenCart,
    IconTruckDelivery,
    IconUser,
    IconSettings,
    IconChevronDown
  } from "@tabler/icons-react";

// const links = [
//   { link: "/home", label: "Home" },
//   { link: "/global", label: "Global" },
//   { link: "/category", label: "Category" },
//   { link: "/product-list", label: "Product List" },
//   { link: "/cart", label: "Cart" },
//   { link: "/profile", label: "Profile" },
//   { link: "/settings", label: "Settings" },
// ];

const links = [
    {
      link: "#1",
      icon: <IconHome2 />,
      label: "Home",
    },
    {
      link: "#2",
      icon: <IconWorld />,
      label: "Global",
    },
    {
      link: "#3",
      icon: <IconCategory />,
      label: "Category",
    },
    {
      link: "#4",
      icon: <IconDatabase />,
      label: "Product List",
    },
    {
      link: "#5",
      icon: <IconGardenCart />,
      label: "Cart",
    },
    {
      link: "#6",
      icon: <IconTruckDelivery />,
      label: "Order",
    },
    {
      link: "#7",
      icon: <IconUser />,
      label: "Profile",
    },
    {
      link: "#8",
      icon: <IconSettings />,
      label: "Settings",
    },
  ];

const HeaderMenu = () => {
  const [opened, { toggle }] = useDisclosure(false);
  const [active, setActive] = useState(links[0].link);

  const items = links.map((link) => (
    <a
      key={link.label}
      href={link.link}
      className={classes.link}
      data-active={active === link.link || undefined}
      onClick={(event) => {
        event.preventDefault();
        setActive(link.link);
      }}
    >
      <Center>
                {link.icon}
                <span className={classes.linkLabel}>{link.label}</span>
                <IconChevronDown size="0.9rem" stroke={1.5} />
              </Center>
    </a>
  ));

  return (
    <header className={classes.header}>
      <Container size="xl" bg="indigo.6" className={classes.inner}>
        <Group ml="lg" gap={5} visibleFrom="xs">
          {items}
        </Group>

        <Burger opened={opened} onClick={toggle} hiddenFrom="xs" size="sm" />
      </Container>
    </header>
  );
};

export default HeaderMenu;
