import React, { useState } from "react";
import { MantineProvider, createTheme } from "@mantine/core";
import "@mantine/core/styles.css";
import Home from "./views/components/Home";
import HeaderMenu from "./views/components/Header/HeaderMenu";

const theme = createTheme({
  fontFamily: "Open Sans, sans-serif",
  primaryColor: "cyan",
});

function App() {
  const [showHome, setShowHome] = useState(false); // Initially, hide the Home component

  return (
    <MantineProvider theme={theme}>
      <HeaderMenu onHomeClick={() => setShowHome(true)} /> {/* Pass the callback to HeaderMenu */}
      {showHome && <Home />} {/* Display Home component if showHome is true */}
    </MantineProvider>
  );
}

export default App;
