import React, { useState } from "react";
import {
  Box,
  Center,
  Flex,
  ActionIcon,
  Avatar,
  Text,
} from "@mantine/core";
import { IconHome2, IconUser, IconMessageCircle, IconBookmark, IconAlignLeft, IconSearch } from "@tabler/icons-react";

const ResponsivePhone = ({ selectedCategory, categories, handleCategoryClick, flexBlock, isVisible }) => {
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const bgColor = isVisible ? "blue" : "gray.2";

  const blackIcons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  const handleBlackIconClick = (index) => {
    setSelectedBlackIcon(index);
  };

  return (
    <Center>
      <Box
        className="responsive-phone"
        pos="relative"
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          maxWidth: "250px",
          width: "250px",
          height: "500px",
          backgroundColor: "var(--mantine-color-gray-0)",
          border: "2px solid var(--mantine-color-dark-9)",
          borderRadius: "18px",
          boxShadow: "5px 5px 10px rgba(0, 0, 0, 0.5)",
        }}
      >
        {/* Header */}
        <Box
          bg="dark.9"
          style={{
            width: "100%",
            height: "30px",
            borderRadius: "14px 14px 0px 0px",
          }}
        >
          <Center mt="xs">
            <Box
              bg="white"
              style={{
                width: "50px",
                height: "5px",
                borderRadius: "20px",
              }}
            />
          </Center>
        </Box>

        {/* Content Area */}
        <Box
          bg={bgColor}
          style={{
            flex: 1,
            width: "100%",
            border: "2px solid var(--mantine-color-dark-9)",
            display: "flex",
            flexDirection: "column",
          }}
        >
          {flexBlock}

          <Flex
            style={{ flex: 1, background: "var(--mantine-color-gray-2)" }}
          ></Flex>

          {/* Icons */}
          <Flex
            mih={50}
            bg="dark.9"
            justify="space-around"
            align="center"
            direction="row"
            wrap="wrap"
            style={{
              padding: "10px",
            }}
          >
            {blackIcons.map((blackIcon, index) => (
              <ActionIcon
                bg="dark.9"
                radius="md"
                key={index}
                variant={selectedBlackIcon === index ? "transparent" : ""}
                onClick={() => handleBlackIconClick(index)}
              >
                {blackIcon.component}
              </ActionIcon>
            ))}
          </Flex>
        </Box>

        {/* Footer */}
        <Box
          bg="dark.9"
          style={{
            width: "100%",
            height: "30px",
            borderRadius: "0px 0px 14px 14px",
          }}
        >
          <Center mt="xs">
            <Box
              bg="white"
              style={{
                width: "50px",
                height: "5px",
                borderRadius: "20px",
              }}
            />
          </Center>
        </Box>
      </Box>
    </Center>
  );
};

export default ResponsivePhone;
