import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";
import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";
import CustomizeCategory from "./CustomizeCategory";

const CategoryWithProductCustomize = ({ categoryWithProductVisible }) => {
  return (
    <>
      {categoryWithProductVisible && (
        <Box
          m="xs"
          h={`fit-content`}
          bg="gray.2"
          mb="xs"
          style={{ borderRadius: "10px" }}
        >
          <Box
            bg="indigo.5"
            ml="xs"
            mr="xs"
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              backgroundColor: "indigo.5",
              padding: "10px",
              borderRadius: "10px",
            }}
          >
            <Text
              fw={900}
              c={`#FFFFFF`}
              style={{
                flex: "1",
                textAlign: "center",
              }}
            >
              Customize
            </Text>
            <IconX
              size={28}
              style={{
                borderRadius: "50%",
                background: "red",
                color: "white",
                alignSelf: "flex-end",
              }}
            />
          </Box>

          {/* Header Input */}
          <Flex
            ml="xs"
            mr="xs"
            mt="xs"
            mih={50}
            bg="#FFFFFF"
            gap="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
            style={{ borderRadius: "10px" }}
          >
            <Text ml="xs">Header</Text>
            <Input
              size="xs"
              ml="xs"
              mr="xs"
              mb="xs"
              w={`95%`}
              placeholder="Input component"
            />
          </Flex>

          <Accordion m="xs" pb={10} variant="contained">
            {/* Type */}
            <CustomizeCategory />
          </Accordion>
        </Box>
      )}
    </>
  );
};

export default CategoryWithProductCustomize;
