import React from "react";
import { useDisclosure, Text, AppShell, Burger, Group, Popover, Paper, List, ListItem, Divider, Button } from "@mantine/core";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();

  const headerStyles = {
    backgroundColor: "blue", // Set the background color to blue
    borderRadius: "0.5rem", // Apply rounded corners
    padding: "0.5rem", // Add padding to the header
  };

  // Data for navigation items
  const navItems = [
    { label: "Item 1", dropdown: ["Subitem 1", "Subitem 2"] },
    { label: "Item 2", dropdown: ["Subitem 3", "Subitem 4"] },
    { label: "Item 3", dropdown: ["Subitem 5", "Subitem 6"] },
    { label: "Item 4", dropdown: ["Subitem 7", "Subitem 8"] },
    { label: "Item 5", dropdown: ["Subitem 9", "Subitem 10"] },
    { label: "Item 6", dropdown: ["Subitem 11", "Subitem 12"] },
    { label: "Item 7", dropdown: ["Subitem 13", "Subitem 14"] },
    { label: "Item 8", dropdown: ["Subitem 15", "Subitem 16"] },
  ];

  return (
    <AppShell header={{ height: 60 }} padding="md">
      <div style={{ backgroundColor: "blue" }} className="rounded-lg p-2">
        <AppShell.Header style={headerStyles}>
          <Group h="50%" px="md">
            <Text>Them-1</Text>
          </Group>
          <Group h="50%" px="md">
            <Text>Test</Text>
          </Group>

          {/* Burger */}
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
        </AppShell.Header>
        <Group h="50%" px="md">
          {navItems.map((item, index) => (
            <Popover
              key={index}
              position="bottom-start"
              interactionType="hover"
              gutter={10}
              withArrow
              placement="bottom"
              transition="pop-top-left"
              arrowSize={5}
              arrowStyle={{ backgroundColor: "blue" }}
              withArrow
              zIndex={1000}
              padding="sm"
              radius="lg"
            >
              <Popover.Trigger>
                <Button size="sm" variant="light">
                  {item.label}
                </Button>
              </Popover.Trigger>
              <Popover.Content>
                <Paper padding="xs">
                  <List>
                    {item.dropdown.map((subitem, subindex) => (
                      <React.Fragment key={subindex}>
                        <ListItem>{subitem}</ListItem>
                        {subindex !== item.dropdown.length - 1 && <Divider />}
                      </React.Fragment>
                    ))}
                  </List>
                </Paper>
              </Popover.Content>
            </Popover>
          ))}
        </Group>
      </div>
    </AppShell>
  );
};

export default AppShellDemo;
