import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  rem,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Group,
  Slider,
  ActionIcon,
} from "@mantine/core";
import { Dropzone } from "@mantine/dropzone";
import ResponsivePhone from "./ResponsivePhone";
import HeaderMenu from "./Header/HeaderMenu";
import {
  IconPlus,
  IconLetterX,
  IconColumns3,
  IconColumns2,
  IconColumns1,
  IconCircleXFilled,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";
const Home = () => {
  const [selectedIcon, setSelectedIcon] = useState(null);

  const [boxValue, setBoxValue] = useState(1);
  const [paddingValue, setPaddingValue] = useState(1);
  const [marginValue, setMarginValue] = useState(1);
  const [categoryWithProductRadiusValue, setCategoryWithProductRadiusValue] =
    useState(1);
  const [productNameTextSizeValue, setProductNameTextSizeValue] = useState(1);
  const [productRateTextSizeValue, setProductRateTextSizeValue] = useState(1);

  const [categoryListBoxVisible, setCategoryListBoxVisible] = useState(false);
  const [categoryWithProductVisible, setCategoryWithProductVisible] =
    useState(false);

  const handleRadiusChange = (newValue) => {
    setBoxValue(newValue);
  };

  const handleRadiusIncrement = () => {
    const newValue = boxValue + 1;
    if (newValue <= 100) {
      setBoxValue(newValue);
    }
  };

  const handlePaddingChange = (newValue) => {
    setPaddingValue(newValue);
  };

  const handlePaddingIncrement = () => {
    const newValue = paddingValue + 1;
    if (newValue <= 100) {
      setPaddingValue(newValue);
    }
  };

  const handleMarginChange = (newValue) => {
    setMarginValue(newValue);
  };

  const handleMarginIncrement = () => {
    const newValue = marginValue + 1;
    if (newValue <= 100) {
      setMarginValue(newValue);
    }
  };

  const handleCategoryWithProductRadiusChange = (newValue) => {
    setCategoryWithProductRadiusValue(newValue);
  };

  const handleCategoryWithProductRadiusIncrement = () => {
    const newValue = categoryWithProductRadiusValue + 1;
    if (newValue <= 100) {
      setCategoryWithProductRadiusValue(newValue);
    }
  };

  const handleProductNameTextSizeChange = (newValue) => {
    setProductNameTextSizeValue(newValue);
  };

  const handleProductNameTextSizeIncrement = () => {
    const newValue = productNameTextSizeValue + 1;
    if (newValue <= 20) {
      setProductNameTextSizeValue(newValue);
    }
  };

  const handleProductRateTextSizeIncrement = () => {
    const newValue = productNameTextSizeValue + 1;
    if (newValue <= 20) {
      setProductNameTextSizeValue(newValue);
    }
  };

  const handleCategoryListClick = () => {
    setCategoryListBoxVisible(true);
    setCategoryWithProductVisible(false);
  };

  const handleCategoryWithProductClick = () => {
    setCategoryWithProductVisible(true);
    setCategoryListBoxVisible(false);
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon:", component);
    setSelectedIcon(index);
  };

  const textStyles = {
    normal: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "none",
    },
    bold: {
      fontWeight: "bold",
      fontStyle: "normal",
      textDecoration: "none",
    },
    italic: {
      fontWeight: "normal",
      fontStyle: "italic",
      textDecoration: "none",
    },
    underline: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "underline",
    },
  };

  const icons = [
    { name: "IconLetterN", component: <IconLetterN />, style: "normal" },
    { name: "IconBold", component: <IconBold />, style: "bold" },
    { name: "IconItalic", component: <IconItalic />, style: "italic" },
    { name: "IconUnderline", component: <IconUnderline />, style: "underline" },
  ];

  const [showProductNameEdit, setShowProductNameEdit] = useState(false);
  const [showProductRateEdit, setShowProductRateEdit] = useState(false);
  const [showCategoryNameEdit, setShowCategoryNameEdit] = useState(false);

  const handleProductNameEdiClick = () => {
    setShowProductNameEdit(!showProductNameEdit);
    setShowProductRateEdit(false);
  };

  const handleProductRateEdiClick = () => {
    setShowProductRateEdit(!showProductRateEdit);
    setShowProductNameEdit(false);
  };

  const handleCategoryNameEdiClick = () => {
    setShowCategoryNameEdit(!showCategoryNameEdit);
    setShowProductRateEdit(false);
    setShowProductNameEdit(false);
  };

  return (
    <Grid>
      <Grid.Col span={4}>
        <Box m="xs" bg="gray.2" style={{ borderRadius: "10px" }}>
          <Flex
            mih={50}
            mt="xs"
            bg="indigo.5"
            gap="md"
            justify="center"
            align="center"
            direction="row"
            wrap="wrap"
            style={{ borderRadius: "10px" }}
          >
            <Text fw={900} c={`#FFFFFF`}>
              Layouts
            </Text>
            <IconLetterX
              style={{
                // border: "1px solid #000",
                borderRadius: "50%",
                background: "red",
                color: "white",
              }}
            />
          </Flex>
          {/* Slider */}
          <Accordion ml="xs" mr="xs" variant="contained">
            <Accordion.Item value="photos">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Slider</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
          </Accordion>
          {/* Category List */}
          <Accordion ml="xs" mr="xs" variant="contained">
            <Accordion.Item value="photos">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Category List</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel bg="gray.2">
                <Box
                  mb="xs"
                  bg="#FFFFFF"
                  onClick={handleCategoryListClick}
                  style={{ cursor: "pointer" }}
                >
                  <Flex
                    h={100}
                    justify="center"
                    align="center"
                    direction="row"
                    gap="md"
                  >
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                      />
                      <Text size="xs">Electronics</Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress"
                      />
                      <Text size="xs">Accessories</Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528"
                      />
                      <Text size="xs">Clothing</Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg"
                      />
                      <Text size="xs">Shoes</Text>
                    </Flex>
                  </Flex>
                </Box>
                <Box
                  bg="dark"
                  onClick={handleCategoryListClick}
                  style={{ cursor: "pointer" }}
                >
                  <Flex
                    h={100}
                    justify="center"
                    align="center"
                    direction="row"
                    gap="md"
                  >
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Electronics
                      </Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Accessories
                      </Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Clothing
                      </Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Shoes
                      </Text>
                    </Flex>
                    {/* <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                        />
                        <Text size="xs">Galaxy</Text>
                      </Flex> */}
                  </Flex>
                </Box>
                <Box
                  bg="dark"
                  mt="xs"
                  onClick={handleCategoryListClick}
                  style={{ cursor: "pointer" }}
                >
                  <Flex
                    h={50}
                    justify="center"
                    align="center"
                    direction="row"
                    gap="md"
                  >
                    <Flex
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                    >
                      <Badge color="blue">All Product</Badge>
                      <Badge color="dark.4">Electronics</Badge>
                      <Badge color="dark.4">Clothing</Badge>
                    </Flex>
                  </Flex>
                </Box>
              </Accordion.Panel>
            </Accordion.Item>
          </Accordion>
          {/* Category With Product */}
          <Accordion ml="xs" mr="xs" variant="contained">
            <Accordion.Item value="photos">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Category With Product</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>
                <Box
                  bg="gray.2"
                  onClick={handleCategoryWithProductClick}
                  style={{ cursor: "pointer" }}
                >
                  <Text ml="xs">Hot Sales</Text>
                  <Flex
                    h={220}
                    gap="2px"
                    justify="flex-start"
                    align="center"
                    direction="row"
                    wrap="wrap"
                  >
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={90}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w={70}
                          fit="contain"
                          src="https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={90}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w={70}
                          fit="contain"
                          src="https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={90}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w={70}
                          fit="contain"
                          src="https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                  </Flex>
                </Box>

                <Box
                  bg="gray.2"
                  mt="xs"
                  onClick={handleCategoryWithProductClick}
                  style={{ cursor: "pointer" }}
                >
                  <Text ml="xs">Hot Sales</Text>
                  <Flex
                    h={440}
                    gap="2px"
                    justify="flex-start"
                    align="center"
                    direction="row"
                    wrap="wrap"
                  >
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={150}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          radius="md"
                          ml="xs"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={150}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card
                      ml="xs"
                      mt="xs"
                      shadow="sm"
                      padding="xs"
                      radius="md"
                      w={150}
                    >
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card
                      ml="xs"
                      mt="xs"
                      shadow="sm"
                      padding="xs"
                      radius="md"
                      w={150}
                    >
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                  </Flex>
                </Box>
              </Accordion.Panel>
            </Accordion.Item>
          </Accordion>
          {/* Brand List */}
          <Accordion ml="xs" mr="xs" variant="contained">
            <Accordion.Item value="photos">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Brand List</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
          </Accordion>
          {/* Brand With Product */}
          <Accordion ml="xs" mr="xs" variant="contained">
            <Accordion.Item value="photos">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Brand With Product</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
          </Accordion>
          {/* Latest Product/ Recently Added */}
          <Accordion ml="xs" mr="xs" variant="contained">
            <Accordion.Item value="photos">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Latest Product/ Recently Added</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
          </Accordion>
          {/* Most Populer Product */}
          <Accordion ml="xs" mr="xs" mb="xs" variant="contained">
            <Accordion.Item value="photos">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Most Populer Product</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
          </Accordion>
        </Box>
      </Grid.Col>
      <Grid.Col span={4} mt="xs">
        <ResponsivePhone />
      </Grid.Col>
      <Grid.Col span={4}>
        {/* Category List Customize Box */}
        {categoryListBoxVisible && (
          <Box
            m="xs"
            h={`fit-content`}
            bg="gray.2"
            style={{ borderRadius: "10px" }}
          >
            <Flex
              mih={50}
              mt="xs"
              bg="indigo.5"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ borderRadius: "10px" }}
            >
              <Text size="xl" c={`#FFFFFF`}>
                Customize
              </Text>
            </Flex>
            {/* Category Item */}
            <Accordion
              m="xs"
              mb="xs"
              variant="contained"
              defaultValue="category-item"
            >
              <Accordion.Item value="category-item">
                <Accordion.Control bg="#FFFFFF">
                  <Text>Category Item</Text>
                </Accordion.Control>
                <Accordion.Panel bg="#FFFFFF">
                  <Accordion m="xs" variant="contained">
                    <Accordion.Item value="electronics">
                      <Accordion.Control bg="gray.2" direction="row">
                        <Flex
                          justify="flex-start"
                          align="center"
                          direction="row"
                          gap="md"
                        >
                          <Checkbox />
                          <Text>Electronics</Text>
                        </Flex>
                      </Accordion.Control>
                      <Accordion.Panel>
                        <Dropzone
                          bg="white"
                          h="5rem"
                          style={{
                            border: "1px solid #1b1c1b",
                            borderRadius: "5px",
                          }}
                        >
                          <Text mt="1.5rem" ta="center">
                            Select Image
                          </Text>
                        </Dropzone>
                        <Flex direction="column">
                          <Text mt="xs">Background Color</Text>
                          <ColorPicker
                            mt="xs"
                            w="auto"
                            size="lg"
                            format="rgba"
                          />
                        </Flex>
                        <Flex direction="column">
                          <Text mt="xs">Level</Text>
                          <Input placeholder="Input component" />
                        </Flex>
                      </Accordion.Panel>
                    </Accordion.Item>
                  </Accordion>

                  <Accordion m="xs" variant="contained">
                    <Accordion.Item value="photos">
                      <Accordion.Control bg="gray.2" direction="row">
                        <Flex
                          justify="flex-start"
                          align="center"
                          direction="row"
                          gap="md"
                        >
                          <Checkbox />
                          <Text>Accessories</Text>
                        </Flex>
                      </Accordion.Control>
                      <Accordion.Panel>
                        <Dropzone
                          bg="white"
                          h="5rem"
                          style={{
                            border: "1px solid #1b1c1b",
                            borderRadius: "5px",
                          }}
                        >
                          <Text mt="1.5rem" ta="center">
                            Select Image
                          </Text>
                        </Dropzone>
                        <Flex direction="column">
                          <Text mt="xs">Background Color</Text>
                          <ColorPicker
                            mt="xs"
                            w="auto"
                            size="lg"
                            format="rgba"
                          />
                        </Flex>
                        <Flex direction="column">
                          <Text mt="xs">Level</Text>
                          <Input placeholder="Input component" />
                        </Flex>
                      </Accordion.Panel>
                    </Accordion.Item>
                  </Accordion>

                  <Accordion m="xs" variant="contained">
                    <Accordion.Item value="photos">
                      <Accordion.Control bg="gray.2" direction="row">
                        <Flex
                          justify="flex-start"
                          align="center"
                          direction="row"
                          gap="md"
                        >
                          <Checkbox />
                          <Text>Clothing</Text>
                        </Flex>
                      </Accordion.Control>
                      <Accordion.Panel>
                        <Dropzone
                          bg="white"
                          h="5rem"
                          style={{
                            border: "1px solid #1b1c1b",
                            borderRadius: "5px",
                          }}
                        >
                          <Text mt="1.5rem" ta="center">
                            Select Image
                          </Text>
                        </Dropzone>
                        <Flex direction="column">
                          <Text mt="xs">Background Color</Text>
                          <ColorPicker
                            mt="xs"
                            w="auto"
                            size="lg"
                            format="rgba"
                          />
                        </Flex>
                        <Flex direction="column">
                          <Text mt="xs">Level</Text>
                          <Input placeholder="Input component" />
                        </Flex>
                      </Accordion.Panel>
                    </Accordion.Item>
                  </Accordion>

                  <Accordion m="xs" variant="contained">
                    <Accordion.Item value="photos">
                      <Accordion.Control bg="gray.2" direction="row">
                        <Flex
                          justify="flex-start"
                          align="center"
                          direction="row"
                          gap="md"
                        >
                          <Checkbox />
                          <Text>Shoes</Text>
                        </Flex>
                      </Accordion.Control>
                      <Accordion.Panel>
                        <Dropzone
                          bg="white"
                          h="5rem"
                          style={{
                            border: "1px solid #1b1c1b",
                            borderRadius: "5px",
                          }}
                        >
                          <Text mt="1.5rem" ta="center">
                            Select Image
                          </Text>
                        </Dropzone>
                        <Flex direction="column">
                          <Text mt="xs">Background Color</Text>
                          <ColorPicker
                            mt="xs"
                            w="auto"
                            size="lg"
                            format="rgba"
                          />
                        </Flex>
                        <Flex direction="column">
                          <Text mt="xs">Level</Text>
                          <Input placeholder="Input component" />
                        </Flex>
                      </Accordion.Panel>
                    </Accordion.Item>
                  </Accordion>

                  <Button ml="xs" w={`95%`}>
                    + Add More
                  </Button>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
            {/* settings */}
            <Accordion m="xs" mb="xs" variant="contained">
              <Accordion.Item value="photos">
                <Accordion.Control bg="#FFFFFF">
                  <Text>Settings</Text>
                </Accordion.Control>
                <Accordion.Panel bg="#FFFFFF">
                  <Stack>
                    {/* Box Radius */}
                    <Text>Box Radius</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        value={boxValue}
                        w={50}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={boxValue}
                        onChange={handleRadiusChange}
                        min={0}
                        max={100}
                        w={180}
                        label={null}
                      />
                      <Button onClick={handleRadiusIncrement}>+</Button>
                    </Flex>
                    {/* padding */}
                    <Text>Padding</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        value={paddingValue}
                        w={50}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={paddingValue}
                        onChange={handlePaddingChange}
                        min={0}
                        max={100}
                        w={180}
                        label={null}
                      />
                      <Button onClick={handlePaddingIncrement}>+</Button>
                    </Flex>
                    {/* Margin */}
                    <Text>Margin</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        value={marginValue}
                        w={50}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={marginValue}
                        onChange={handleMarginChange}
                        min={0}
                        max={100}
                        w={200}
                        label={null}
                      />
                      <IconPlus
                        onClick={handleMarginIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                        }}
                      />
                    </Flex>
                  </Stack>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
          </Box>
        )}
        {/* Category With Product Customize Box */}
        {categoryWithProductVisible && (
          <Box
            m="xs"
            h={`fit-content`}
            bg="gray.2"
            style={{ borderRadius: "10px" }}
          >
            <Flex
              mih={50}
              mt="xs"
              bg="indigo.5"
              gap="md"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{ borderRadius: "10px" }}
            >
              <Text size="xl" c={`#FFFFFF`}>
                Customize
              </Text>
            </Flex>
            <Flex
              ml="xs"
              mr="xs"
              mt="xs"
              mih={50}
              bg="#FFFFFF"
              gap="xs"
              justify="center"
              align="flex-start"
              direction="column"
              wrap="wrap"
              style={{ borderRadius: "10px" }}
            >
              <Text ml="xs">Header</Text>
              <Input
                size="xs"
                ml="xs"
                mr="xs"
                mb="xs"
                w={`95%`}
                placeholder="Input component"
              />
            </Flex>
            {/* Type */}
            <Accordion ml="xs" mr="xs" mt="5px" variant="contained">
              <Accordion.Item value="photos">
                <Accordion.Control bg="#FFFFFF">Type</Accordion.Control>
                <Accordion.Panel bg="#FFFFFF">
                  <Flex
                    mih={50}
                    gap="xs"
                    justify="center"
                    align="center"
                    direction="row"
                    wrap="wrap"
                  >
                    <Flex
                      mih={50}
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{ border: "1px solid #000", borderRadius: "10px" }}
                    >
                      <IconColumns1 size={50} />
                      <IconColumns1 size={50} />
                      <IconColumns1 size={50} />
                    </Flex>
                    <Flex
                      mih={50}
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{ border: "1px solid #000", borderRadius: "10px" }}
                    >
                      <IconColumns1 size={50} />
                      <IconColumns1 size={50} />
                    </Flex>
                    <Flex
                      mih={50}
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{ border: "1px solid #000", borderRadius: "10px" }}
                    >
                      <IconColumns1 size={50} />
                    </Flex>
                  </Flex>
                  <Flex
                    mih={50}
                    gap="xs"
                    justify="center"
                    align="center"
                    direction="row"
                    wrap="wrap"
                  >
                    <Stack
                      mt="5px"
                      w={`150`}
                      h={`100`}
                      gap="0"
                      style={{ border: "1px solid #000", borderRadius: "10px" }}
                    >
                      <Flex>
                        <IconColumns1 size={50} />
                        <IconColumns1 size={50} />
                        <IconColumns1 size={50} />
                      </Flex>
                      <Flex>
                        <IconColumns1 size={50} />
                        <IconColumns1 size={50} />
                        <IconColumns1 size={50} />
                      </Flex>
                    </Stack>
                    <Stack
                      ml="5px"
                      mt="5px"
                      w={`100`}
                      h={`100`}
                      gap="0"
                      style={{ border: "1px solid #000", borderRadius: "10px" }}
                    >
                      <Flex>
                        <IconColumns1 size={50} />
                        <IconColumns1 size={50} />
                      </Flex>
                      <Flex>
                        <IconColumns1 size={50} />
                        <IconColumns1 size={50} />
                      </Flex>
                    </Stack>
                    <Stack
                      ml="5px"
                      mt="5px"
                      w={`50`}
                      h={`100`}
                      gap="0"
                      style={{ border: "1px solid #000", borderRadius: "10px" }}
                    >
                      <Flex>
                        <IconColumns1 size={50} />
                      </Flex>
                      <Flex>
                        <IconColumns1 size={50} />
                      </Flex>
                    </Stack>
                  </Flex>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
            {/* Cart Option */}
            <Accordion ml="xs" mr="xs" mt="5px" variant="contained">
              <Accordion.Item value="photos">
                <Accordion.Control bg="#FFFFFF">Cart Options</Accordion.Control>
                <Accordion.Panel bg="#FFFFFF">
                  <Flex direction="column">
                    <Text mt="xs">Background Color</Text>
                    <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
                  </Flex>
                  <Stack>
                    {/* Box Radius */}
                    <Text>Box Radius</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        value={categoryWithProductRadiusValue}
                        w={50}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={categoryWithProductRadiusValue}
                        onChange={handleCategoryWithProductRadiusChange}
                        min={0}
                        max={100}
                        w={180}
                        label={null}
                      />
                      <Button
                        onClick={handleCategoryWithProductRadiusIncrement}
                      >
                        +
                      </Button>
                    </Flex>
                  </Stack>
                  <Stack mt="xs">
                    {/* Product Name */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Product Name</Text>
                        <Checkbox onClick={handleProductNameEdiClick} checked={showProductNameEdit} />
                      </Flex>
                      {showProductNameEdit && (
                        <Stack
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text mt="xs">Text Color</Text>
                            <ColorPicker
                              mt="xs"
                              w="auto"
                              size="lg"
                              format="rgba"
                            />
                          </Flex>
                          <Stack mt="xs" ml="xs" mr="xs">
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                value={productNameTextSizeValue}
                                w={50}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                my="xs"
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={180}
                                label={null}
                              />
                              <Button
                                onClick={handleProductNameTextSizeIncrement}
                              >
                                +
                              </Button>
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text mt="xs">Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                            <Stack
                              m="xs"
                              bg="white"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              <Text ml="xs">Text Content</Text>
                              <Text
                                ml="xs"
                                style={
                                  textStyles[
                                    icons[selectedIcon]?.style || "normal"
                                  ]
                                }
                              >
                                Your text content goes here.
                              </Text>
                            </Stack>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Product Rate */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Product Ratee</Text>
                        <Checkbox onClick={handleProductRateEdiClick} checked={showProductRateEdit} />
                      </Flex>
                      {showProductRateEdit && (
                        <Stack
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text mt="xs">Text Color</Text>
                            <ColorPicker
                              mt="xs"
                              w="auto"
                              size="lg"
                              format="rgba"
                            />
                          </Flex>
                          <Stack mt="xs" ml="xs" mr="xs">
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                value={productNameTextSizeValue}
                                w={50}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                my="xs"
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={180}
                                label={null}
                              />
                              <Button
                                onClick={handleProductNameTextSizeIncrement}
                              >
                                +
                              </Button>
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text mt="xs">Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                            <Stack
                              m="xs"
                              bg="white"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              <Text ml="xs">Text Content</Text>
                              <Text
                                ml="xs"
                                style={
                                  textStyles[
                                    icons[selectedIcon]?.style || "normal"
                                  ]
                                }
                              >
                                Your text content goes here.
                              </Text>
                            </Stack>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Category Name */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Category Name</Text>
                        <Checkbox onClick={handleCategoryNameEdiClick} checked={showCategoryNameEdit} />
                      </Flex>
                      {showCategoryNameEdit && (
                      <Stack
                        style={{
                          border: "1px solid #000",
                          borderRadius: "10px",
                        }}
                      >
                        <Flex ml="xs" mr="xs" direction="column">
                          <Text mt="xs">Text Color</Text>
                          <ColorPicker
                            mt="xs"
                            w="auto"
                            size="lg"
                            format="rgba"
                          />
                        </Flex>
                        <Stack mt="xs" ml="xs" mr="xs">
                          {/* Box Radius */}
                          <Text>Text Size</Text>
                          <Flex
                            mih={50}
                            bg="white"
                            gap="md"
                            justify="center"
                            align="center"
                            direction="row"
                            wrap="wrap"
                          >
                            <Input
                              type="number"
                              value={productNameTextSizeValue}
                              w={50}
                              readOnly
                              style={{ borderRadius: "20px" }}
                            />
                            <Slider
                              my="xs"
                              value={productNameTextSizeValue}
                              onChange={handleProductNameTextSizeChange}
                              min={0}
                              max={20}
                              w={180}
                              label={null}
                            />
                            <Button
                              onClick={handleProductNameTextSizeIncrement}
                            >
                              +
                            </Button>
                          </Flex>
                        </Stack>
                        <Flex ml="xs" mr="xs" direction="column">
                          <Text mt="xs">Text Font</Text>
                          <Flex
                            bg="#FFFFFF"
                            h={50}
                            style={{
                              border: "1px solid #000",
                              borderRadius: "10px",
                            }}
                          ></Flex>
                        </Flex>
                        <Stack>
                          <Text ml="xs">Text Style</Text>
                          <Flex
                            mih={50}
                            bg="white"
                            ml="xs"
                            mr="xs"
                            gap="md"
                            justify="center"
                            align="center"
                            direction="row"
                            wrap="wrap"
                            style={{
                              border: "1px solid #1b1c1b",
                              borderRadius: "5px",
                            }}
                          >
                            {icons.map((icon, index) => (
                              <ActionIcon
                                key={index}
                                variant={
                                  selectedIcon === index ? "outline" : "default"
                                }
                                aria-label={`Icon ${icon.component}`}
                                onClick={() => handleIconClick(index, icon)}
                              >
                                {icon.component}
                              </ActionIcon>
                            ))}
                          </Flex>
                          <Stack
                            m="xs"
                            bg="white"
                            style={{
                              border: "1px solid #1b1c1b",
                              borderRadius: "5px",
                            }}
                          >
                            <Text ml="xs">Text Content</Text>
                            <Text
                              ml="xs"
                              style={
                                textStyles[
                                  icons[selectedIcon]?.style || "normal"
                                ]
                              }
                            >
                              Your text content goes here.
                            </Text>
                          </Stack>
                        </Stack>
                      </Stack>
                      )}
                    </Stack>
                    {/* Add Cart Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Add Cart Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Offer */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Offer</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Favorite Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Favorite Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Ratting */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Ratting</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                  </Stack>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
          </Box>
        )}
      </Grid.Col>
    </Grid>
  );
};

export default Home;
