import React, { useState } from "react";
import { Grid, MantineProvider, createTheme } from "@mantine/core";
import "@mantine/core/styles.css";
import NavbarSegmented from "./views/components/SideNavbar/NavbarSegmented";
import ResponsivePhone from "./views/components/ResponsivePhone";
const theme = createTheme({
  fontFamily: "Open Sans, sans-serif",
  primaryColor: "cyan",
});

function App() {
  const [isStack1Selected, setIsStack1Selected] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [appbarContent, setAppbarContent] = useState(null); // Store Appbar content

  // Function to handle item selection
  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  return (
    <MantineProvider theme={theme}>
      <Grid>
        <Grid.Col span={4}>
          <NavbarSegmented
            isStack1Selected={isStack1Selected}
            setIsStack1Selected={setIsStack1Selected}
            isStack2Selected={isStack2Selected}
            setIsStack2Selected={setIsStack2Selected}
            onItemClick={handleItemClick} // Pass the item selection handler
          />
        </Grid.Col>
        <Grid.Col span={4} mt="xs">
          <ResponsivePhone selectedItem={selectedItem} />
        </Grid.Col>
        <Grid.Col span={4}></Grid.Col>
      </Grid>
    </MantineProvider>
  );
}

export default App;
