import React, { useState } from "react";
import { Accordion } from "@mantine/core";
import { IconMinus, IconPlus } from "@tabler/icons-react";

const Receipts = () => {
  const [openItem, setOpenItem] = useState(null);

  const handleAccordionChange = (value) => {
    setOpenItem(value === openItem ? null : value);
  };

  const renderAccordionItem = (value, label, children) => (
    <Accordion.Item key={value} value={value} onToggle={handleAccordionChange}>
      <Accordion.Control>
        {openItem === value ? (
          <IconMinus size={`15px`} />
        ) : (
          <IconPlus size={`15px`} />
        )}
        {label}
      </Accordion.Control>
      <Accordion.Panel>{children}</Accordion.Panel>
    </Accordion.Item>
  );

  return (
    <Accordion>
      {renderAccordionItem(
        "apple",
        "Apple",
        <Accordion>
          {renderAccordionItem(
            "test",
            "Test",
            <Accordion>
              {renderAccordionItem("test-2", "Test-2", "Content")}
            </Accordion>
          )}
        </Accordion>
      )}
    </Accordion>
  );
};

export default Receipts;
