import React, { useState } from "react";
import { ColorPicker, Grid, Box } from "@mantine/core";

const Layout = () => {

    const [layout, setLayout] = useState({
        background_color: "#cdcdcd",
        box_background_color: "#cdcdcd",
        border_radius: 2,
        heading_text: "Hello",
        items_coloum: 3,
        category: "new",
        shadow: 2
      })
    
      const updateProperty = (property, value) => {
        setLayout({...layout, [property] : value})
      }

  return (
    <Grid>
      <Grid.Col span={6}>
        <Box bg={layout.background_color} h={300} w={`100%`}>
          <Box bg={layout.box_background_color} h={100} w={`100%`}></Box>
        </Box>
      </Grid.Col>
      <Grid.Col span={6}>
        <ColorPicker onChange={(value) => updateProperty('background_color', value )} />
      </Grid.Col>
    </Grid>
  )
}

export default Layout
