import React, { useState } from "react";
import { Grid } from "@mantine/core";
import Layout from "./Layout";
import Layout2 from "./Layout2";
import Layout3 from "./Layout3";

const Index = () => {
  const [bgColor, setBgColor] = useState("gray.4");
  const [boxBorderRadius, setBoxBorderRadius] = useState(5);
  const [boxShadow, setBoxShadow] = useState("0 4px 6px rgba(0, 0, 0, 0.1)");
  const [text, setText] = useState("Test"); // Initial text content
  const [textBgColor, setTextBgColor] = useState("lime.3"); // Initial text background color
  const [textBorderRadius, setTextBorderRadius] = useState(5); // Initial text border radius
  const [gridColumns, setGridColumns] = useState(3); // Initial number of grid columns

  const handleColorChange = (color) => {
    setBgColor(color);
  };

  const handleBorderRadiusChange = (value) => {
    setBoxBorderRadius(value);
  };

  const handleBoxShadowChange = (shadow) => {
    setBoxShadow(shadow);
  };

  const handleTextChange = (newText) => {
    setText(newText);
  };

  const handleTextBgColorChange = (color) => {
    setTextBgColor(color);
  };

  const handleTextBorderRadiusChange = (value) => {
    setTextBorderRadius(value);
  };

  const handleGridColumnsChange = (columns) => {
    setGridColumns(columns);
  };

  return (
    <Grid>
      <Grid.Col span={4}>
        <Layout
          bgColor={bgColor}
          borderRadius={boxBorderRadius}
          boxShadow={boxShadow}
          text={text}
          textBgColor={textBgColor}
          textBorderRadius={textBorderRadius}
          gridColumns={gridColumns}
        />
      </Grid.Col>
      <Grid.Col span={4}>
        <Layout2 />
      </Grid.Col>
      <Grid.Col span={4}>
        <Layout3
          onColorChange={handleColorChange}
          boxBorderRadius={boxBorderRadius}
          onBorderRadiusChange={handleBorderRadiusChange}
          boxShadow={boxShadow}
          onBoxShadowChange={handleBoxShadowChange}
          text={text}
          onTextChange={handleTextChange}
          textBgColor={textBgColor}
          onTextBgColorChange={handleTextBgColorChange}
          textBorderRadius={textBorderRadius}
          onTextBorderRadiusChange={handleTextBorderRadiusChange}
          gridColumns={gridColumns}
          onGridColumnsChange={handleGridColumnsChange}
        />
      </Grid.Col>
    </Grid>
  );
};

export default Index;
