import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";
import { Dropzone } from "@mantine/dropzone";
import ResponsivePhone from "./ResponsivePhone";
import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";
const Home = () => {
  const [selectedIcon, setSelectedIcon] = useState(null);

  const [boxValue, setBoxValue] = useState(1);
  const [paddingValue, setPaddingValue] = useState(1);
  const [marginValue, setMarginValue] = useState(1);
  const [categoryWithProductRadiusValue, setCategoryWithProductRadiusValue] =
    useState(1);
  const [productNameTextSizeValue, setProductNameTextSizeValue] = useState(1);

  const [categoryListBoxVisible, setCategoryListBoxVisible] = useState(false);
  const [categoryWithProductVisible, setCategoryWithProductVisible] =
    useState(false);

  const [categoryWithProductHeaderValue, setCategoryWithProductHeaderValue] =
    useState("Hot Sales");

  const handleHeaderChange = (event) => {
    const newValue = event.target.value;
    setCategoryWithProductHeaderValue(newValue);
  };

  const [showCheckbox, setShowCheckbox] = useState(false);
  const [checkboxChecked, setCheckboxChecked] = useState(false);

  const handleRadiusChange = (newValue) => {
    setBoxValue(newValue);
  };

  const handleRadiusIncrement = () => {
    const newValue = boxValue + 1;
    if (newValue <= 100) {
      setBoxValue(newValue);
    }
  };

  const handlePaddingChange = (newValue) => {
    setPaddingValue(newValue);
  };

  const handlePaddingIncrement = () => {
    const newValue = paddingValue + 1;
    if (newValue <= 100) {
      setPaddingValue(newValue);
    }
  };

  const handleMarginChange = (newValue) => {
    setMarginValue(newValue);
  };

  const handleMarginIncrement = () => {
    const newValue = marginValue + 1;
    if (newValue <= 100) {
      setMarginValue(newValue);
    }
  };

  const handleCategoryWithProductRadiusChange = (newValue) => {
    setCategoryWithProductRadiusValue(newValue);
  };

  const handleCategoryWithProductRadiusIncrement = () => {
    const newValue = categoryWithProductRadiusValue + 1;
    if (newValue <= 100) {
      setCategoryWithProductRadiusValue(newValue);
    }
  };

  const handleProductNameTextSizeChange = (newValue) => {
    setProductNameTextSizeValue(newValue);
  };

  const handleProductNameTextSizeIncrement = () => {
    const newValue = productNameTextSizeValue + 1;
    if (newValue <= 20) {
      setProductNameTextSizeValue(newValue);
    }
  };

  const handleCategoryListClick = () => {
    setCategoryListBoxVisible(true);
    setCategoryWithProductVisible(false);
  };

  const handleCategoryWithProductClick = () => {
    setCategoryWithProductVisible(!categoryWithProductVisible);
    setCategoryListBoxVisible(false);
    setShowCheckbox(!showCheckbox);
    setCheckboxChecked(!checkboxChecked);
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon:", component);
    setSelectedIcon(index);
  };

  const textStyles = {
    normal: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "none",
    },
    bold: {
      fontWeight: "bold",
      fontStyle: "normal",
      textDecoration: "none",
    },
    italic: {
      fontWeight: "normal",
      fontStyle: "italic",
      textDecoration: "none",
    },
    underline: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "underline",
    },
  };

  const icons = [
    { name: "IconLetterN", component: <IconLetterN />, style: "normal" },
    { name: "IconBold", component: <IconBold />, style: "bold" },
    { name: "IconItalic", component: <IconItalic />, style: "italic" },
    { name: "IconUnderline", component: <IconUnderline />, style: "underline" },
  ];

  const [showProductNameEdit, setShowProductNameEdit] = useState(false);
  const [showProductRateEdit, setShowProductRateEdit] = useState(false);
  const [showCategoryNameEdit, setShowCategoryNameEdit] = useState(false);

  const handleProductNameEdiClick = () => {
    setShowProductNameEdit(!showProductNameEdit);
    setShowProductRateEdit(false);
  };

  const handleProductRateEdiClick = () => {
    setShowProductRateEdit(!showProductRateEdit);
    setShowProductNameEdit(false);
  };

  const handleCategoryNameEdiClick = () => {
    setShowCategoryNameEdit(!showCategoryNameEdit);
    setShowProductRateEdit(false);
    setShowProductNameEdit(false);
  };

  return (
    <Grid>
      <Grid.Col span={4}>
        <Box m="xs" bg="gray.2" style={{ borderRadius: "10px" }}>
          {/* Layouts */}
          <Box
            bg="indigo.5"
            ml="xs"
            mr="xs"
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              backgroundColor: "indigo.5",
              padding: "10px",
              borderRadius: "10px",
            }}
          >
            <Text
              fw={900}
              c={`#FFFFFF`}
              style={{
                flex: "1",
                textAlign: "center",
              }}
            >
              Layouts
            </Text>
            <IconX
              size={28}
              style={{
                borderRadius: "50%",
                background: "red",
                color: "white",
                alignSelf: "flex-end",
              }}
            />
          </Box>
          {/* Slider */}
          <Accordion ml="xs" mr="xs" variant="contained">
            <Accordion.Item value="slider">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Slider</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
            {/* Category List */}
            <Accordion.Item value="category-list">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Category List</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel bg="gray.2">
                <Box
                  mb="xs"
                  bg="#FFFFFF"
                  onClick={handleCategoryListClick}
                  style={{ cursor: "pointer" }}
                >
                  
                  
                  <Flex
                    h={100}
                    justify="center"
                    align="center"
                    direction="row"
                    gap="md"
                  >
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                      />
                      <Text size="xs">Electronics</Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress"
                      />
                      <Text size="xs">Accessories</Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528"
                      />
                      <Text size="xs">Clothing</Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg"
                      />
                      <Text size="xs">Shoes</Text>
                    </Flex>
                  </Flex>
                </Box>
                <Box
                  bg="dark"
                  onClick={handleCategoryListClick}
                  style={{ cursor: "pointer" }}
                >
                  <Flex
                    h={100}
                    justify="center"
                    align="center"
                    direction="row"
                    gap="md"
                  >
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Electronics
                      </Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Accessories
                      </Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Clothing
                      </Text>
                    </Flex>
                    <Flex justify="center" align="center" direction="column">
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src="https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg"
                      />
                      <Text size="xs" c={"gray.0"}>
                        Shoes
                      </Text>
                    </Flex>
                    {/* <Flex justify="center" align="center" direction="column">
                        <Avatar
                          variant="filled"
                          radius="sm"
                          size="lg"
                          color="yellow"
                          src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                        />
                        <Text size="xs">Galaxy</Text>
                      </Flex> */}
                  </Flex>
                </Box>
                <Box
                  bg="dark"
                  mt="xs"
                  onClick={handleCategoryListClick}
                  style={{ cursor: "pointer" }}
                >
                  <Flex
                    h={50}
                    justify="center"
                    align="center"
                    direction="row"
                    gap="md"
                  >
                    <Flex
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                    >
                      <Badge color="blue">All Product</Badge>
                      <Badge color="dark.4">Electronics</Badge>
                      <Badge color="dark.4">Clothing</Badge>
                    </Flex>
                  </Flex>
                </Box>
              </Accordion.Panel>
            </Accordion.Item>
            {/* Category With Product */}
            <Accordion.Item value="category-with-product">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Category With Product</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>
                <Box
                  bg="gray.2"
                  onClick={handleCategoryWithProductClick}
                  style={{ cursor: "pointer" }}
                >
                  <Flex justify="space-between" align="center" direction="row">
                    <Text ml="xs">Hot Sales</Text>
                    {showCheckbox && (
                      <Checkbox
                        checked={checkboxChecked}
                        onChange={(event) =>
                          setCheckboxChecked(event.target.checked)
                        }
                        mr="xs"
                      />
                    )}
                  </Flex>
                  <Flex
                    h={220}
                    gap="2px"
                    justify="flex-start"
                    align="center"
                    direction="row"
                    wrap="wrap"
                  >
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={90}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w={70}
                          fit="contain"
                          src="https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={90}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w={70}
                          fit="contain"
                          src="https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={90}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w={70}
                          fit="contain"
                          src="https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                  </Flex>
                </Box>

                <Box
                  bg="gray.2"
                  mt="xs"
                  onClick={handleCategoryWithProductClick}
                  style={{ cursor: "pointer" }}
                >
                  <Flex justify="space-between" align="center" direction="row">
                    <Text ml="xs">{categoryWithProductHeaderValue}</Text>
                    {showCheckbox && (
                      <Checkbox
                        checked={checkboxChecked}
                        onChange={(event) =>
                          setCheckboxChecked(event.target.checked)
                        }
                        mr="xs"
                      />
                    )}
                  </Flex>
                  <Flex
                    h={440}
                    gap="2px"
                    justify="flex-start"
                    align="center"
                    direction="row"
                    wrap="wrap"
                  >
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={150}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          radius="md"
                          ml="xs"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card ml="xs" shadow="sm" padding="xs" radius="md" w={150}>
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card
                      ml="xs"
                      mt="xs"
                      shadow="sm"
                      padding="xs"
                      radius="md"
                      w={150}
                    >
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                    <Card
                      ml="xs"
                      mt="xs"
                      shadow="sm"
                      padding="xs"
                      radius="md"
                      w={150}
                    >
                      <Badge color="pink" variant="light" mb="xs">
                        On Sale
                      </Badge>
                      <Card.Section>
                        <Image
                          ml="xs"
                          mr="xs"
                          radius="md"
                          h={100}
                          w="auto"
                          fit="contain"
                          src="https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg"
                          alt="Norway"
                        />
                      </Card.Section>
                      <Text fw={500}>Dell</Text>
                      <Text fw={500}>$29,999</Text>
                    </Card>
                  </Flex>
                </Box>
              </Accordion.Panel>
            </Accordion.Item>
            {/* Brand List */}
            <Accordion.Item value="brand-list">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Brand List</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
            {/* Brand With Product */}
            <Accordion.Item value="brand-with-product">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Brand With Product</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
            {/* Latest Product/ Recently Added */}
            <Accordion.Item value="latest -product">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Latest Product/ Recently Added</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
            {/* Most Populer Product */}
            <Accordion.Item value="most-populer-product">
              <Accordion.Control bg="indigo.2">
                <Center>
                  <Text fw={700}>Most Populer Product</Text>
                </Center>
              </Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
          </Accordion>
        </Box>
      </Grid.Col>
      <Grid.Col span={4} mt="xs">
        <ResponsivePhone />
      </Grid.Col>
      <Grid.Col span={4}>
        {/* Category List Customize Box */}
        {categoryListBoxVisible && (
          <Box
            m="xs"
            h={`fit-content`}
            bg="gray.2"
            style={{ borderRadius: "10px" }}
          >
            <Box
              bg="indigo.5"
              ml="xs"
              mr="xs"
              style={{
