import { Box, Stack, Text, Center, Grid } from "@mantine/core";
import React from "react";

const Layout = ({ bgColor, borderRadius, boxShadow, text, textBgColor }) => {
  const items = ["Item 1", "Item 2", "Item 3"];
  const textBorderRadius = -borderRadius; // Calculate the inverse of the main box radius
  return (
    
      <Stack  bg={bgColor}
      h={200}
      style={{
        borderRadius: `${borderRadius}px`,
        boxShadow: boxShadow,
      }}>
        {/* <Box
          bg={textBgColor}
          style={{ borderRadius: `${textBorderRadius}px ${textBorderRadius}px 0 0` }}
        >
          <Center>
            <Text>{text}</Text>
          </Center>
        </Box> */}
        <Grid ml="xs" mr="xs">
          {items.map((item) => (
            <Grid.Col span={4}>
              <Box
                bg="blue.1"
                h={60}
                style={{
                  border: "1px solid #000000",
                  boxShadow: boxShadow,
                }}
              >
                <Center>{item}</Center>
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
  );
};

export default Layout;
