import React from "react";
import {
  Avatar,
  Text,
  Flex,
  Box,
  Grid,
  Center,
  rem,
} from "@mantine/core";
import { IconAlignLeft, IconSearch, IconBell } from "@tabler/icons-react";

const appbarItems = [
  {
    id: 1,
    icon: <IconAlignLeft />,
    content: <Text size="xs">AppBar 1</Text>,
  },
  {
    id: 2,
    icon: <IconSearch size={`15px`} />,
    content: (
      <Box
        bg="gray.1"
        px={3}
        c={`gray.4`}
        style={{
          width: "150px",
          backgroundColor: "var(--mantine-color-gray-0)",
          border: "1px solid var(--mantine-color-gray-4)",
          borderRadius: "15px",
        }}
      >
        <Flex gap="xs" justify="flex-start" align="center" direction="row" wrap="wrap">
          <IconSearch style={{ width: rem(15), height: rem(15) }} stroke={1.5} />
          <Text>Search</Text>
        </Flex>
      </Box>
    ),
  },
  {
    id: 3,
    icon: (
      <Avatar
        size="sm"
        bg="white"
        radius="md"
        src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=250&q=80"
      />
    ),
    content: <Text size="xs">AppBar 3</Text>,
  },
];

const AppBar = ({ selectedItem, setItem }) => {
  const handleItemClick = (item) => {
    setItem(item);
  };

  return (
    <Grid>
      {appbarItems.map((item) => (
        <Grid.Col key={item.id} span={6}>
          <Box onClick={() => handleItemClick(item)}>
            <Center>
              {item.icon}
            </Center>
            <Center>
              {item.content}
            </Center>
          </Box>
        </Grid.Col>
      ))}
    </Grid>
  );
};

export default AppBar;
