import React, { useState } from "react";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  rem,
  Avatar,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Stack>
      <Center>
        <Box
          style={{
            border: "2px solid #1b1c1b",
            borderRadius: "15px",
            width: "200px", // Fixed width
            height: "400px", // Fixed height
            backgroundColor: "#ffffff",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Stack
            bg="gray.2"
            style={{
              flex: 0,
              border: "1px solid #1b1c1b",
              borderRadius: "13px 13px 0 0",
            }}
          >
            <Flex mih={50} gap="md" justify="space-between" align="center">
              <div style={{ flex: 1 }}>
                <IconAlignLeft style={{ marginLeft: "4px" }} />
              </div>
              <div style={{ display: "flex", alignItems: "center", gap: "md" }}>
                <Autocomplete
                  placeholder="Search"
                  leftSection={
                    <IconSearch
                      style={{ width: rem(16), height: rem(16) }}
                      stroke={1.5}
                    />
                  }
                  visibleFrom="xs"
                  radius="lg"
                />
              </div>
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "md",
                  marginRight: "4px",
                }}
              >
                <Avatar bg="white" radius="xl">
                  <IconBell />
                </Avatar>
              </div>
            </Flex>
          </Stack>

          <Box
            style={{
              flex: 1, // Push content to the top
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            {/* Mobile frame content here */}
          </Box>
          <Flex
            bg="dark.9"
            direction={{ base: "column", sm: "row" }}
            gap={{ base: "sm", sm: "lg" }}
            justify={{ base: "center", sm: "flex-end" }} // Adjust justify based on your responsive needs
            style={{
              
              border: "1px solid #1b1c1b",
              borderRadius: "0 0 13px 13px",
              padding: "12px",
            }}
          >
            {blackicons.map((blackicon, index) => (
              <ActionIcon
                bg="black"
                radius="md"
                key={index}
                variant={selectedBlackIcon === index ? "transparent" : ""}
                aria-label={`Icon ${blackicon.component}`}
                onClick={() => handleBlackIconClick(index, blackicon)}
              >
                {blackicon.component}
              </ActionIcon>
            ))}
          </Flex>
        </Box>
      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
