import { Box, Center, Container, HoverCard } from "@mantine/core";
import React from "react";

const AppBar = () => {
  return (
    <HoverCard
      width={600}
      position="bottom"
      radius="md"
      shadow="md"
      withinPortal
    >
      <Center inline>
        <Container component="span" mr={5} bg={"var(--mantine-color-blue-4)"} widthfull>
          App Bar
        </Container>
      </Center>
    </HoverCard>
  );
};

export default AppBar;
