import { Box, Center, Grid, Stack, Text } from "@mantine/core";
import React, { useState } from "react";

const componentStyles = {
  box: {
    borderRadius: "10px",
    background: "#CED4DA",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    height: 200,
  },
  headerBox: {
    background: "#94D82D",
    borderRadius: "10px 10px 0 0",
    cursor: "pointer",
  },
  itemBox: {
    background: "#D0EBFF",
    border: "1px solid #000000",
    cursor: "pointer",
    height: 30,
  },
};

const Layout3 = () => {
  const items = ["Item 1", "Item 2", "Item 3"];

  // State variable to hold the input value
  const [inputValue, setInputValue] = useState("Test");

  return (
    <Box style={componentStyles.box}>
      <Stack>
        <Box style={componentStyles.headerBox}>
          <Center>
            {/* Input element with value and onChange */}
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
            />
          </Center>
        </Box>
        <Grid ml="xs" mr="xs">
          {items.map((item) => (
            <Grid.Col span={4}>
              <Box style={componentStyles.itemBox}>
                <Center>{item}</Center>
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout3;
