              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              labelAlwaysOn
              mb="xs"
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>

      <Stack mb="xs">
        <Text style={{ marginLeft: "1rem" }}>Margin</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

