                      <Slider
                        my="xs"
                        value={categoryWithProductRadiusValue}
                        onChange={handleCategoryWithProductRadiusChange}
                        min={0}
                        max={100}
                        w={180}
                        label={null}
                      />
                      <Button
                        onClick={handleCategoryWithProductRadiusIncrement}
                      >
                        +
                      </Button>
                    </Flex>
                  </Stack>
                  <Stack mt="xs" gap={5}>
                    {/* Product Name */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Product Name</Text>
                        <Checkbox
                          onClick={handleProductNameEdiClick}
                          checked={showProductNameEdit}
                        />
                      </Flex>
                      {showProductNameEdit && (
                        <Stack
                          gap={0}
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Color</Text>
                            <ColorPicker w="auto" size="lg" format="rgba" />
                          </Flex>
                          <Stack ml="xs" mr="xs" gap={0}>
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                value={productNameTextSizeValue}
                                w={50}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={170}
                                label={null}
                              />
                              <Button
                                onClick={handleProductNameTextSizeIncrement}
                              >
                                +
                              </Button>
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack gap={0}>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              mb="xs"
                              gap="md"
                              justify="space-around"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Product Rate */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Product Rate</Text>
                        <Checkbox
                          onClick={handleProductRateEdiClick}
                          checked={showProductRateEdit}
                        />
                      </Flex>
                      {showProductRateEdit && (
                        <Stack
                          gap={0}
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Color</Text>
                            <ColorPicker
                              mt="xs"
                              w="auto"
                              size="lg"
                              format="rgba"
                            />
                          </Flex>
                          <Stack mt="xs" ml="xs" mr="xs">
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                value={productNameTextSizeValue}
                                w={50}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                my="xs"
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={170}
                                label={null}
                              />
                              <Button
                                onClick={handleProductNameTextSizeIncrement}
                              >
                                +
                              </Button>
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              mb="xs"
                              gap="md"
                              justify="space-around"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Category Name */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Category Name</Text>
                        <Checkbox
                          onClick={handleCategoryNameEdiClick}
                          checked={showCategoryNameEdit}
                        />
                      </Flex>
                      {showCategoryNameEdit && (
                        <Stack
                          gap={0}
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Color</Text>
                            <ColorPicker w="auto" size="lg" format="rgba" />
                          </Flex>
                          <Stack ml="xs" mr="xs" gap={0}>
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                value={productNameTextSizeValue}
                                w={50}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                my="xs"
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={170}
                                label={null}
                              />
                              <Button
                                onClick={handleProductNameTextSizeIncrement}
                              >
                                +
                              </Button>
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack gap={0}>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              mb="xs"
                              gap="md"
                              justify="space-around"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Add Cart Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Add Cart Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Offer */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Offer</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Favorite Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Favorite Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Ratting */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Ratting</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                  </Stack>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
          </Box>
        )}
      </Grid.Col>
    </Grid>
  );
};

export default Home;
