import React, { useState } from "react";
import { Box, Center, Flex, ActionIcon } from "@mantine/core";
import {
  IconCircleXFilled,
  IconHome2,
  IconUser,
  IconSearch,
  IconMessageCircle,
  IconShoppingBag,
  IconBookmark,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Center>
      <Box
        style={{
          border: "2px solid #1b1c1b",
          borderRadius: "8px",
          width: "200px", // Fixed width
          height: "400px", // Fixed height
          backgroundColor: "#ffffff",
          boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <div style={{ flex: "1" }}>
          {/* Add empty div to push content to the top */}
        </div>
        <Flex
          mih="50px" // Set the minimum height
          bg="dark.9"
          ml="xl"
          mr="xl"
          justify="space-around"
          align="center"
          direction="row"
          wrap="wrap"
          style={{
            border: "1px solid #1b1c1b",
            borderRadius: "0 0 25px 25px",
            padding: "10px",
          }}
        >
          {blackicons.map((blackicon, index) => (
            <ActionIcon
              bg="black"
              radius="md"
              key={index}
              variant={selectedBlackIcon === index ? "transparent" : ""}
              aria-label={`Icon ${blackicon.component}`}
              onClick={() => handleBlackIconClick(index, blackicon)}
            >
              {blackicon.component}
            </ActionIcon>
          ))}
        </Flex>
      </Box>
    </Center>
  );
};

export default ResponsivePhone;
