import React, { useState } from "react";
import {
  Text,
  ColorPicker,
  Input,
  Slider,
  IconPlus,
  Checkbox,
  ActionIcon,
  Accordion
} from "@mantine/core";
import { IconBold, IconUnderline, IconItalic, IconLetterN } from "@tabler/icons-react";

const CustomizeCategory = () => {
  const [categoryWithProductRadiusValue, setCategoryWithProductRadiusValue] = useState(1);
  const handleCategoryWithProductRadiusChange = (newValue) => {
    setCategoryWithProductRadiusValue(newValue);
  };

  const handleCategoryWithProductRadiusIncrement = () => {
    const newValue = categoryWithProductRadiusValue + 1;
    if (newValue <= 100) {
      setCategoryWithProductRadiusValue(newValue);
    }
  };

  const customizationOptions = [
    {
      title: "Cart Options",
      content: (
        <div>
          <Text>Background Color</Text>
          <ColorPicker w="auto" size="lg" format="rgba" />
        </div>
      ),
    },
    {
      title: "Product Name",
      content: (
        <div>
          <Text>Text Color</Text>
          <ColorPicker w="auto" size="lg" format="rgba" />
          {/* Add more customization options for Product Name */}
        </div>
      ),
    },
    {
      title: "Product Rate",
      content: (
        <div>
          <Text>Text Color</Text>
          <ColorPicker w="auto" size="lg" format="rgba" />
          {/* Add more customization options for Product Rate */}
        </div>
      ),
    },
    {
      title: "Category Name",
      content: (
        <div>
          <Text>Text Color</Text>
          <ColorPicker w="auto" size="lg" format="rgba" />
          {/* Add more customization options for Category Name */}
        </div>
      ),
    },
  ];

  return (
    <>
      {customizationOptions.map((option, index) => (
        <Accordion.Item key={index} value={`customization-${option.title.toLowerCase()}`}>
          <Accordion.Control bg="#FFFFFF">{option.title}</Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            {option.content}
          </Accordion.Panel>
        </Accordion.Item>
      ))}
    </>
  );
};

export default CustomizeCategory;
