import React from "react";
import { Link } from "react-router-dom"; // Import Link from React Router
import { Box, Center, Grid, Stack, Text } from "@mantine/core";

const Layout = ({ selected, onItemSelect }) => {
  const items = ["Item 1", "Item 2", "Item 3"];

  return (
    <Box bg="gray.4" h={200} style={{ borderRadius: "10px" }}>
      <Stack spacing={0}>
        <Link to="/layout2"> {/* Use Link to navigate to Layout2 */}
          <Box bg="lime.3" style={{ borderRadius: "10px 10px 0 0" }}>
            <Center>
              <Text>Test</Text>
            </Center>
          </Box>
        </Link>
        <Grid ml="xs" mr="xs">
          {items.map((item, index) => (
            <Grid.Col span={4} key={index}>
              <Box
                bg={selected === item ? "blue.1" : "white"}
                h={60}
                style={{ border: "1px solid #000000" }}
                onClick={() => onItemSelect(item)}
              >
                <Center>{item}</Center>
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout;
