import { Box, Center, Grid, Stack, Text } from "@mantine/core";
import React, { useState } from "react";
// import Layout2 from "./Layout2";

const Layout = () => {
  const items = ["Item 1", "Item 2", "Item 3"];
  const [selected, setSelected] = useState(null);

  const handleItemClick = (item) => {
    setSelected(item);
  };

  return (
    <Stack spacing={0}>
      <Box bg="gray.4" h={200} style={{ borderRadius: "10px" }}>
        <Box
          bg={selected === "Test" ? "blue.1" : "lime.3"}
          style={{ borderRadius: "10px 10px 0 0", cursor: "pointer" }}
          onClick={() => handleItemClick("Test")}
        >
          <Center>
            <Text>Test</Text>
          </Center>
        </Box>
        <Grid ml="xs" mr="xs">
          {items.map((item, index) => (
            <Grid.Col span={4} key={index}>
              <Box
                bg={selected === item ? "blue.1" : "white"}
                h={60}
                style={{ border: "1px solid #000000", cursor: "pointer" }}
                onClick={() => handleItemClick(item)}
              >
                <Center>{item}</Center>
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Box>
      <Layout2 selected={selected} />
    </Stack>
  );
};

export default Layout;
