import React from "react";
import { Box, Stack, Text, Center, Grid } from "@mantine/core";

const Layout = ({ bgColor, borderRadius, columns }) => {
  const columnArray = Array.from({ length: columns }, (_, i) => i + 1);

  return (
    <Box p={20} bg={bgColor} h={200} style={{ borderRadius: `${borderRadius}px` }}>
      <Stack spacing={0}>
        <Grid ml="xs" mr="xs">
          {columnArray.map((colIndex) => (
            <Grid.Col span={1} key={colIndex} >
              <Box bg="blue.1" h={60} style={{ border: "1px solid #000000" }}></Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout;
