import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Badge,
  Checkbox,
  Image,
  Card,
} from "@mantine/core";

const BrandWithProduct = ({
  setBoxVisible,
  boxVisible,
  setBoxVisible2,
  boxVisible2,
  title,
  title2,
}) => {
  const [iconCheckVisible, setIconCheckVisible] = useState(false);
  const [iconCheckVisible2, setIconCheckVisible2] = useState(false);

  const handleCategoryWithProductClick = (index) => {
    setBoxVisible(!boxVisible);
    setBoxVisible2(false);
    setIconCheckVisible(!iconCheckVisible);
    setIconCheckVisible2(false);
  };

  const handleCategoryWithProductClick2 = (index) => {
    setBoxVisible2(!boxVisible2);
    setBoxVisible(false);
    setIconCheckVisible2(!iconCheckVisible2);
    setIconCheckVisible(false);
  };

  const items = [
    {
      title: title,
      iconCheckVisible: iconCheckVisible,
      handleCategoryWithProductClick: handleCategoryWithProductClick,
      cards: [
        {
          imageSrc:
            "https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323",
          alt: "Norway",
          badgeText: "On Sale",
          brand: "Dell",
          price: "$29,999",
          width: 90,
        },
        {
          imageSrc:
            "https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg",
          alt: "Norway",
          badgeText: "On Sale",
          brand: "Dell",
          price: "$29,999",
          width: 90,
        },
        {
          imageSrc:
            "https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg",
          alt: "Norway",
          badgeText: "On Sale",
          brand: "Dell",
          price: "$29,999",
          width: 90,
        },
      ],
    },
    {
      title: title2,
      iconCheckVisible: iconCheckVisible2,
      handleCategoryWithProductClick: handleCategoryWithProductClick2,
      cards: [
        {
          imageSrc:
            "https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323",
          alt: "Norway",
          badgeText: "On Sale",
          brand: "Dell",
          price: "$29,999",
          width: 145,
        },
        {
          imageSrc:
            "https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg",
          alt: "Norway",
          badgeText: "On Sale",
          brand: "Dell",
          price: "$29,999",
          width: 145,
        },
        {
          imageSrc:
            "https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg",
          alt: "Norway",
          badgeText: "On Sale",
          brand: "Dell",
          price: "$29,999",
          width: 145,
        },
        {
          imageSrc:
            "https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg",
          alt: "Norway",
          badgeText: "On Sale",
          brand: "Dell",
          price: "$29,999",
          width: 145,
        },
      ],
    },
  ];

  return (
    <Accordion.Item value="brand-with-product">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Brand With Product</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel>
        {items.map((item, index) => (
          <Box
            key={index}
            bg="gray.2"
            style={{ cursor: "pointer" }}
            onClick={() => item.handleCategoryWithProductClick(index)}
          >
            <Flex justify="space-between" align="center" direction="row">
              <Text ml="xs">{item.title}</Text>
              {item.iconCheckVisible && (
                <Flex mr="xs" h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
            <Flex
              h={item.title === title ? 220 : 440}
              gap="2px"
              justify="flex-start"
              align="center"
              direction="row"
              wrap="wrap"
            >
              {item.cards.map((card, cardIndex) => (
                <Card key={cardIndex} ml="xs" shadow="sm" padding="xs" radius="md" w={card.width}>
                  <Badge color="pink" variant="light" mb="xs">
                    {card.badgeText}
                  </Badge>
                  <Card.Section>
                    <Image
                      ml="xs"
                      mr="xs"
                      radius="md"
                      h={100}
                      w="auto"
                      fit="contain"
                      src={card.imageSrc}
                      alt={card.alt}
                    />
                  </Card.Section>
                  <Text fw={500}>{card.brand}</Text>
                  <Text fw={500}>{card.price}</Text>
                </Card>
              ))}
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default BrandWithProduct;
