import React, { useState } from "react";
import { useDisclosure } from "@mantine/hooks";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  rem,
  Avatar,
  Text,
  Button,
  LoadingOverlay,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  const [visible, { toggle }] = useDisclosure(true);
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Stack>
      <Center>
      <Box
  className="responsive-phone"
  pos="relative"
  style={{
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    maxWidth: "250px",
    width: "250px",
    height: "500px",
    backgroundColor: "#ffffff",
    border: "2px solid #1b1c1b",
    borderRadius: "18px",
    boxShadow: "5px 5px 10px rgba(0, 0, 0, 0.2)",
  }}
>
  <Box bg="dark.2" style={{ height: "50px", width: "100%" }}>
    {/* This is the first Box with a height of 50px */}
  </Box>
  <Box bg="gray.1" style={{ flex: 1, width: "100%", position: "relative" }}>
    <LoadingOverlay
      visible={visible}
      zIndex={1000}
      overlayProps={{ radius: "15px", blur: 2 }}
      loaderProps={{ color: "pink", type: "bars" }}
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {/* This is the center Box with flexible height */}
    </LoadingOverlay>
    <Flex
                mih={50}
                gap="xs"
                justify="center"
                align="center"
                direction="row"
                wrap="wrap"
              >
                <IconAlignLeft />
                <Box
                  bg="gray.1"
                  p={3}
                  c={`gray.4`}
                  style={{
                    width: "150px",
                    backgroundColor: "#ffffff",
                    border: "1px solid #cdcdcd",
                    borderRadius: "15px",
                  }}
                >
                  <Flex
                    // mih={50}
                    // bg="rgba(0, 0, 0, .3)"
                    gap="xs"
                    justify="flex-start"
                    align="center"
                    direction="row"
                    wrap="wrap"
                  >
                    <IconSearch
                      style={{ width: rem(16), height: rem(16) }}
                      stroke={1.5}
                    />
                    <Text>Search</Text>
                  </Flex>
                </Box>
                <Avatar bg="white" radius="xl">
                  <IconBell />
                </Avatar>
              </Flex>
  </Box>
  <Box bg="dark.2" style={{ height: "50px", width: "100%" }}>
    {/* This is the last Box with a height of 50px */}
  </Box>
</Box>

      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
