import React, { useState } from "react";
import { Accordion } from "@mantine/core";
import { IconPlus, IconMinus } from "@tabler/icons-react";

const Receipts = () => {
  const [isAccordion1Open, setIsAccordion1Open] = useState(false);
  const [isAccordion2Open, setIsAccordion2Open] = useState(false);

  const handleAccordion1Toggle = () => {
    setIsAccordion1Open((prev) => !prev);
  };

  const handleAccordion2Toggle = () => {
    setIsAccordion2Open((prev) => !prev);
  };

  return (
    <Accordion
      bg={`gray.3`}
      chevron={isAccordion1Open ? < IconPlus /> : <IconMinus />}
      onToggle={handleAccordion1Toggle}
    >
      <Accordion.Item value="apple">
        <Accordion.Control>Apple</Accordion.Control>
        <Accordion.Panel>
          <Accordion
            chevron={isAccordion2Open ? <IconMinus /> : <IconPlus />}
            onToggle={handleAccordion2Toggle}
            bg={`blue`}
          >
            <Accordion.Item value="test">
              <Accordion.Control>Test</Accordion.Control>
              <Accordion.Panel>
                <Accordion chevron={null} bg={`red`}>
                  <Accordion.Item value="test-2">
                    <Accordion.Control>Test-2</Accordion.Control>
                    <Accordion.Panel>Content</Accordion.Panel>
                  </Accordion.Item>
                </Accordion>
              </Accordion.Panel>
            </Accordion.Item>
          </Accordion>
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default Receipts;
