import React from "react";
import { Box, Paper } from "@mantine/core";

const MobileFrame = ({ children }) => {
  return (
    <Box
      style={{
        border: "2px solid #1b1c1b",
        borderRadius: "15px",
        width: "275px", // Adjust this width to match your desired mobile screen size
        height: "367px", // Adjust this height to match your desired mobile screen size
        margin: "0 auto", // Center the frame horizontally
        backgroundColor: "#ffffff",
        boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <Paper padding="md" shadow="xs">
        {children}
      </Paper>
    </Box>
  );
};

export default MobileFrame;
