import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";

import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";

const CustomizeCategoryList4 = () => {
  {
    /* Dropzone */
  }

  const [files, setFiles] = useState([]);

  const previews = files.map((file, index) => {
    const imageUrl = URL.createObjectURL(file);
    return (
      <Image
        key={index}
        src={imageUrl}
        onLoad={() => URL.revokeObjectURL(imageUrl)}
      />
    );
  });

  const [categoryHeader, setCategoryHeader] = useState({
    technology_level: "Technology",
    fashion_level: "Fashion",
    sports_level: "Sports",
    home_level: "Home",
  });

  const updateProperty = (property, value) => {
    setCategoryHeader({ ...categoryHeader, [property]: value });
  };

  const onTechnologyChange = (text) => {
    updateProperty("technology_level", text);
  };

  const onFashionChange = (text) => {
    updateProperty("fashion_level", text);
  };

  const onSportsChange = (text) => {
    updateProperty("sports_level", text);
  };

  const onHomeChange = (text) => {
    updateProperty("home_level", text);
  };
  return (
    <Accordion m="xs" variant="contained">
      {/* Technology */}
      <Accordion.Item value="technology">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Technology</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.technology_level}
              onChange={(event) => {
                onTechnologyChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
      {/* Fashion */}
      <Accordion.Item value="fashion">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Fashion</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.fashion_level}
              onChange={(event) => {
                onFashionChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
      {/* Sports */}
      <Accordion.Item value="sports">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Sports</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.sports_level}
              onChange={(event) => {
                onSportsChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
      {/* Home */}
      <Accordion.Item value="home">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Home</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.home_level}
              onChange={(event) => {
                onHomeChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>

      {/* Add More */}
      <Accordion.Item mt="xs" value="addmore">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Text c={`indigo.6`}>+ Add More</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input placeholder="Input component" />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default CustomizeCategoryList4;
