import React, {useState} from "react";
import {
  Box,
  Stack,
  Flex,
  Slider,
  Input,
  Text,
  Accordion,
} from "@mantine/core";

import {
  IconX,
  IconPlus,
} from "@tabler/icons-react";
import CustomizeCategoryList1 from "./CustomizeCategoryList1";

const Customize = ({categoryListBoxVisible}) => {

  
  
  return (
    <>
    {categoryListBoxVisible &&(
    <Box m="xs" h={`fit-content`} bg="gray.2" style={{ borderRadius: "10px" }}>
      
      <Box
        bg="indigo.5"
        ml="xs"
        mr="xs"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "indigo.5",
          padding: "10px",
          borderRadius: "10px",
        }}
      >
        <Text
          fw={900}
          c={`#FFFFFF`}
          style={{
            flex: "1",
            textAlign: "center",
          }}
        >
          Customize
        </Text>
        <IconX
          size={28}
          style={{
            borderRadius: "50%",
            background: "red",
            color: "white",
            alignSelf: "flex-end",
          }}
        />
      </Box>
      <Accordion
        m="xs"
        variant="contained"
        defaultValue="category-item"
        pb={10}
      >
        <Accordion.Item value="category-item">
          <Accordion.Control bg="#FFFFFF">
            <Text>Category Item</Text>
          </Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            <CustomizeCategoryList1 />
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </Box>
    )}
    </>
  );
};

export default Customize;
