import React, { useState } from "react";
import {
  Text,
  Flex,
  Accordion,
  Checkbox,
  ColorPicker,
  Input,
  Stack,
  Slider,
  ActionIcon,
} from "@mantine/core";
import {
  IconPlus,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";

const CustomizeCategory = () => {
  const [categoryWithProductRadiusValue, setCategoryWithProductRadiusValue] =
    useState(1);

  const handleCategoryWithProductRadiusChange = (newValue) => {
    setCategoryWithProductRadiusValue(newValue);
  };

  const handleCategoryWithProductRadiusIncrement = () => {
    const newValue = categoryWithProductRadiusValue + 1;
    if (newValue <= 100) {
      setCategoryWithProductRadiusValue(newValue);
    }
  };

  const [showProductNameEdit, setShowProductNameEdit] = useState(false);
  const [showProductRateEdit, setShowProductRateEdit] = useState(false);
  const [showCategoryNameEdit, setShowCategoryNameEdit] = useState(false);

  const handleProductNameEdiClick = () => {
    setShowProductNameEdit(!showProductNameEdit);
  };

  const handleProductRateEdiClick = () => {
    setShowProductRateEdit(!showProductRateEdit);
  };

  const handleCategoryNameEdiClick = () => {
    setShowCategoryNameEdit(!showCategoryNameEdit);
  };
  const [productNameTextSizeValue, setProductNameTextSizeValue] = useState(1);
  const handleProductNameTextSizeChange = (newValue) => {
    setProductNameTextSizeValue(newValue);
  };
  const handleProductNameTextSizeIncrement = () => {
    const newValue = productNameTextSizeValue + 1;
    if (newValue <= 20) {
      setProductNameTextSizeValue(newValue);
    }
  };
  const [selectedIcon, setSelectedIcon] = useState(0);
  const icons = [
    { name: "IconLetterN", component: <IconLetterN />, style: "normal" },
    { name: "IconBold", component: <IconBold />, style: "bold" },
    { name: "IconItalic", component: <IconItalic />, style: "italic" },
    { name: "IconUnderline", component: <IconUnderline />, style: "underline" },
  ];
  const handleIconClick = (index, component) => {
    console.log("Clicked icon:", component);
    setSelectedIcon(index);
  };

  const [selectionStates, setSelectionStates] = useState([
    true,
    false,
    false,
    false,
    false,
    false,
  ]);

  const toggleSelection = (index) => {
    const newSelectionStates = selectionStates.map((state, i) =>
      i === index ? true : false
    );
    setSelectionStates(newSelectionStates);
  };
  return (
    <>
      <Accordion.Item value="type">
        <Accordion.Control bg="#FFFFFF">Type</Accordion.Control>
        <Accordion.Panel bg="#FFFFFF">
          <ActionIcon
            variant={selectionStates[0] ? "filled" : "default"}
            onClick={() => toggleSelection(0)}
            color="indigo"
            w={150}
            h={50}
          >
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[1] ? "filled" : "default"}
            onClick={() => toggleSelection(1)}
            color="indigo"
            w={100}
            h={50}
            ml="xs"
          >
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[2] ? "filled" : "default"}
            onClick={() => toggleSelection(2)}
            color="indigo"
            w={50}
            h={50}
            ml="xs"
          >
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[3] ? "filled" : "default"}
            onClick={() => toggleSelection(3)}
            color="indigo"
            w={150}
            h={100}
          >
            <Stack w="100%" gap={0}>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
            </Stack>
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[4] ? "filled" : "default"}
            onClick={() => toggleSelection(4)}
            color="indigo"
            w={100}
            h={100}
            ml="xs"
          >
            <Stack w="100%" gap={0}>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
            </Stack>
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[5] ? "filled" : "default"}
            onClick={() => toggleSelection(5)}
            color="indigo"
            w={50}
            h={100}
            ml="xs"
          >
            <Stack w="100%" gap={0}>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
            </Stack>
          </ActionIcon>
        </Accordion.Panel>
      </Accordion.Item>
      <Accordion.Item value="cart-options">
        <Accordion.Control bg="#FFFFFF">Cart Options</Accordion.Control>
        <Accordion.Panel bg="#FFFFFF">
          <Flex direction="column">
            <Text>Background Color</Text>
            <ColorPicker w="auto" size="lg" format="rgba" />
          </Flex>

          {/* Box Radius */}
          <Stack gap={0}>
  <Text>Box Radius</Text>
  <Flex
    mih={40}
    bg="white"
    gap="md"
    justify="center"
    align="center"
    direction="row"
    wrap="wrap"
    style={{
      border: "1px solid #1b1c1b",
      borderRadius: "5px",
    }}
  >
    <Input
      type="number"
      value={categoryWithProductRadiusValue}
      w={["100%", "45%"]} // Use 100% width on small screens, 45% on larger screens
      size="xs"
      readOnly
      style={{ borderRadius: "20px" }}
    />
    <Slider
      my="xs"
      value={categoryWithProductRadiusValue}
      onChange={handleCategoryWithProductRadiusChange}
      min={0}
      max={100}
      w={["100%", "200%"]} // Use 100% width on small screens, 200px on larger screens
      label={null}
    />
    <IconPlus
      onClick={handleCategoryWithProductRadiusIncrement}
      style={{
        border: "1px solid #000",
        borderRadius: "50%",
        cursor: "pointer",
      }}
    />
  </Flex>
</Stack>


          <Stack gap={0}>
            {/* Product Name */}
            <Stack>
              <Flex
                justify="space-between"
                align="center"
                direction="row"
                wrap="wrap"
              >
                <Text>Product Name</Text>
                <Checkbox
                  onClick={handleProductNameEdiClick}
                  checked={showProductNameEdit}
                />
              </Flex>
              {showProductNameEdit && (
                <Stack
                  gap={0}
                  style={{
                    border: "1px solid #000",
                    borderRadius: "10px",
                  }}
                >
                  <Flex ml="xs" mr="xs" direction="column">
                    <Text>Text Color</Text>
                    <ColorPicker w="auto" size="lg" format="rgba" />
                  </Flex>
                  <Stack ml="xs" mr="xs" gap={0}>
                    {/* Box Radius */}
                    <Text>Text Size</Text>
                    <Flex
                      mih={40}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Input
                        type="number"
                        value={productNameTextSizeValue}
                        size="xs"
                        w={45}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        value={productNameTextSizeValue}
                        onChange={handleProductNameTextSizeChange}
                        min={0}
                        max={20}
                        w={190}
                        label={null}
                      />
                      <IconPlus
                        onClick={handleProductNameTextSizeIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                          cursor: "pointer",
                        }}
                      />
                    </Flex>
                  </Stack>
                  <Flex ml="xs" mr="xs" direction="column">
                    <Text>Text Font</Text>
                    <Flex
                      bg="#FFFFFF"
                      h={40}
                      style={{
                        border: "1px solid #000",
                        borderRadius: "10px",
                      }}
                    ></Flex>
                  </Flex>
                  <Stack gap={0}>
                    <Text ml="xs">Text Style</Text>
                    <Flex
                      mih={40}
                      bg="white"
                      ml="xs"
                      mr="xs"
                      mb="xs"
                      justify="space-around"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      {icons.map((icon, index) => (
                        <ActionIcon
                          key={index}
                          variant={
                            selectedIcon === index ? "outline" : "default"
                          }
                          aria-label={`Icon ${icon.component}`}
                          onClick={() => handleIconClick(index, icon)}
                        >
                          {icon.component}
                        </ActionIcon>
                      ))}
                    </Flex>
                  </Stack>
                </Stack>
              )}
            </Stack>
            {/* Product Rate */}
            <Stack>
              <Flex
                justify="space-between"
                align="center"
                direction="row"
                wrap="wrap"
              >
                <Text>Product Rate</Text>
                <Checkbox
                  onClick={handleProductRateEdiClick}
                  checked={showProductRateEdit}
                />
              </Flex>
              {showProductRateEdit && (
                <Stack
                  gap={0}
                  style={{
                    border: "1px solid #000",
                    borderRadius: "10px",
                  }}
                >
                  <Flex ml="xs" mr="xs" direction="column">
                    <Text>Text Color</Text>
                    <ColorPicker w="auto" size="lg" format="rgba" />
                  </Flex>
                  <Stack ml="xs" mr="xs" gap={0}>
                    {/* Box Radius */}
                    <Text>Text Size</Text>
                    <Flex
                      mih={40}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Input
                        value={productNameTextSizeValue}
                        type="number"
                        w={45}
                        size="xs"
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        value={productNameTextSizeValue}
                        onChange={handleProductNameTextSizeChange}
                        my="xs"
                        min={0}
                        max={20}
                        w={170}
                        label={null}
                      />
                      <IconPlus
                        onClick={handleProductNameTextSizeIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                          cursor: "pointer",
                        }}
                      />
                    </Flex>
                  </Stack>
                  <Flex ml="xs" mr="xs" direction="column">
                    <Text>Text Font</Text>
                    <Flex
                      bg="#FFFFFF"
                      h={40}
                      style={{
                        border: "1px solid #000",
                        borderRadius: "10px",
                      }}
                    ></Flex>
                  </Flex>
                  <Stack>
                    <Text ml="xs">Text Style</Text>
                    <Flex
                      mih={40}
                      bg="white"
                      ml="xs"
                      mr="xs"
                      mb="xs"
                      justify="space-around"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      {icons.map((icon, index) => (
                        <ActionIcon
                          key={index}
                          variant={
                            selectedIcon === index ? "outline" : "default"
                          }
                          aria-label={`Icon ${icon.component}`}
                          onClick={() => handleIconClick(index, icon)}
                        >
                          {icon.component}
                        </ActionIcon>
                      ))}
                    </Flex>
                  </Stack>
                </Stack>
              )}
            </Stack>
            {/* Category Name */}
            <Stack gap={0}>
              <Flex
                justify="space-between"
                align="center"
                direction="row"
                wrap="wrap"
              >
                <Text>Category Name</Text>
                <Checkbox
                  onClick={handleCategoryNameEdiClick}
                  checked={showCategoryNameEdit}
                />
              </Flex>
              {showCategoryNameEdit && (
                <Stack
                  gap={0}
                  style={{
                    border: "1px solid #000",
                    borderRadius: "10px",
                  }}
                >
                  <Flex ml="xs" mr="xs" direction="column">
                    <Text>Text Color</Text>
                    <ColorPicker w="auto" size="lg" format="rgba" />
                  </Flex>
                  <Stack ml="xs" mr="xs" gap={0}>
                    {/* Box Radius */}
                    <Text>Text Size</Text>
                    <Flex
                      mih={40}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Input
                        value={productNameTextSizeValue}
                        type="number"
                        w={45}
                        size="xs"
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        value={productNameTextSizeValue}
                        onChange={handleProductNameTextSizeChange}
                        my="xs"
                        min={0}
                        max={20}
                        w={170}
                        label={null}
                      />
                      <IconPlus
                        onClick={handleProductNameTextSizeIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                          cursor: "pointer",
                        }}
                      />
                    </Flex>
                  </Stack>
                  <Flex ml="xs" mr="xs" direction="column">
                    <Text>Text Font</Text>
                    <Flex
                      bg="#FFFFFF"
                      mih={40}
                      style={{
                        border: "1px solid #000",
                        borderRadius: "10px",
                      }}
                    ></Flex>
                  </Flex>
                  <Stack gap={0}>
                    <Text ml="xs">Text Style</Text>
                    <Flex
                      mih={40}
                      bg="white"
                      ml="xs"
                      mr="xs"
                      mb="xs"
                      justify="space-around"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      {icons.map((icon, index) => (
                        <ActionIcon
                          key={index}
                          variant={
                            selectedIcon === index ? "outline" : "default"
                          }
                          aria-label={`Icon ${icon.component}`}
                          onClick={() => handleIconClick(index, icon)}
                        >
                          {icon.component}
                        </ActionIcon>
                      ))}
                    </Flex>
                  </Stack>
                </Stack>
              )}
            </Stack>
            {/* Add Cart Icon */}
            <Flex
              justify="space-between"
              align="center"
              direction="row"
              wrap="wrap"
            >
              <Text>Add Cart Icon</Text>
              <Checkbox defaultChecked />
            </Flex>
            {/* Offer */}
            <Flex
              justify="space-between"
              align="center"
              direction="row"
              wrap="wrap"
            >
              <Text>Offer</Text>
              <Checkbox defaultChecked />
            </Flex>
            {/* Favorite Icon */}
            <Flex
              justify="space-between"
              align="center"
              direction="row"
              wrap="wrap"
            >
              <Text>Favorite Icon</Text>
              <Checkbox defaultChecked />
            </Flex>
            {/* Ratting */}
            <Flex
              justify="space-between"
              align="center"
              direction="row"
              wrap="wrap"
            >
              <Text>Ratting</Text>
              <Checkbox defaultChecked />
            </Flex>
          </Stack>
        </Accordion.Panel>
      </Accordion.Item>
    </>
  );
};

export default CustomizeCategory;
